import React from 'react';
import Card from '../../../../components/common/layout/Card';

const DocumentCard = ({ title, description, addedDate }) => (
  <Card className="mb-4">
    <div className="p-4">
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h3 className="text-lg font-medium text-gray-900">{title}</h3>
          <p className="mt-1 text-sm text-gray-500">{description}</p>
        </div>
        <div className="ml-4 flex flex-col items-end">
          <button
            type="button"
            className="inline-flex items-center px-3 py-1.5 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            View Document
          </button>
          <span className="mt-2 text-xs text-gray-400">Added on {addedDate}</span>
        </div>
      </div>
    </div>
  </Card>
);

const FoundationDocuments = () => {
  const documents = [
    {
      title: 'Articles of Association',
      description: 'Standard or customised rules for how the company operates',
      addedDate: 'Jan 15, 2024'
    },
    {
      title: 'Shareholder Agreement',
      description: 'Defines rights, responsibilities, and dispute resolution between founders/shareholders',
      addedDate: 'Jan 15, 2024'
    },
    {
      title: 'Founders\' Agreements',
      description: 'Clarifies roles, equity, IP ownership, vesting, and commitments',
      addedDate: 'Jan 15, 2024'
    }
  ];

  return (
    <div>
      <div className="space-y-4">
        {documents.map((doc, index) => (
          <DocumentCard key={index} {...doc} />
        ))}
      </div>

      <div className="mt-6 flex justify-center">
        <button
          type="button"
          className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
        >
          <svg 
            className="-ml-1 mr-2 h-5 w-5" 
            xmlns="http://www.w3.org/2000/svg" 
            viewBox="0 0 20 20" 
            fill="currentColor"
          >
            <path 
              fillRule="evenodd" 
              d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" 
              clipRule="evenodd" 
            />
          </svg>
          Add Document
        </button>
      </div>
    </div>
  );
};

export default FoundationDocuments;
