import React, { useState } from 'react';
import SubNav from '../../components/common/layout/SubNav';
import HRDashboard from './components/HRDashboard';
import HolidayRequests from './components/holiday/HolidayRequests';
import Recruitment from './components/recruitment/Recruitment';

const HR = () => {
  const [activeSubTab, setActiveSubTab] = useState('dashboard');

  const subTabs = [
    { id: 'dashboard', name: 'Dashboard' },
    { id: 'holiday', name: 'Holiday Requests' },
    { id: 'recruitment', name: 'Recruitment' },
  ];

  const renderContent = () => {
    switch (activeSubTab) {
      case 'dashboard':
        return <HRDashboard />;
      case 'holiday':
        return <HolidayRequests />;
      case 'recruitment':
        return <Recruitment />;
      default:
        return <HRDashboard />;
    }
  };

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Human Resources</h1>
      </div>
      <SubNav
        subTabs={subTabs}
        activeSubTab={activeSubTab}
        onSubTabChange={setActiveSubTab}
      />
      <div className="mt-8">
        {renderContent()}
      </div>
    </div>
  );
};

export default HR;
