import React, { useState } from 'react';
import { DocumentTextIcon, MagnifyingGlassIcon } from '@heroicons/react/24/outline';

const templateCategories = [
  {
    name: 'Foundation',
    templates: [
      { name: 'Founder Vesting Agreement', content: 'This Founder Vesting Agreement...' },
      { name: 'Articles of Association', content: 'The Articles of Association for the company...' },
    ],
  },
  {
    name: 'General',
    templates: [
      { name: 'Non-Disclosure Agreement (NDA)', content: 'This Non-Disclosure Agreement (the "Agreement")...' },
      { name: 'Master Services Agreement (MSA)', content: 'This Master Services Agreement...' },
    ],
  },
  {
    name: 'Employee',
    templates: [
      { name: 'Standard Employment Contract', content: 'This Employment Agreement is made between...' },
      { name: 'Share Option Agreement', content: 'This Share Option Agreement grants...' },
    ],
  },
];

const Templates = () => {
  const [selectedTemplate, setSelectedTemplate] = useState(templateCategories[0].templates[0]);
  const [searchTerm, setSearchTerm] = useState('');

  const filteredCategories = templateCategories.map(category => ({
    ...category,
    templates: category.templates.filter(template =>
      template.name.toLowerCase().includes(searchTerm.toLowerCase())
    ),
  })).filter(category => category.templates.length > 0);

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
      {/* Left Column */}
      <div className="md:col-span-1 space-y-6">
        <div className="relative">
          <MagnifyingGlassIcon className="pointer-events-none absolute top-3.5 left-4 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search templates..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-11 pr-4 py-2 border border-gray-300 rounded-md"
          />
        </div>
        <div className="space-y-4">
          {filteredCategories.map(category => (
            <div key={category.name}>
              <h3 className="text-xs font-semibold uppercase text-gray-500 mb-2">{category.name}</h3>
              <ul className="space-y-1">
                {category.templates.map(template => (
                  <li key={template.name}>
                    <button
                      onClick={() => setSelectedTemplate(template)}
                      className={`w-full flex items-center p-3 text-left text-sm rounded-md transition-colors ${
                        selectedTemplate.name === template.name
                          ? 'bg-blue-100 text-blue-700'
                          : 'text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      <DocumentTextIcon className="h-5 w-5 mr-3" />
                      {template.name}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>

      {/* Right Column */}
      <div className="md:col-span-2">
        <div className="bg-white p-6 rounded-lg shadow border border-gray-200 sticky top-8">
          {selectedTemplate ? (
            <div className="space-y-6">
              <div className="flex justify-between items-center">
                <h2 className="text-xl font-bold text-gray-900">{selectedTemplate.name}</h2>
                <button className="px-4 py-2 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700">
                  Customise Template
                </button>
              </div>
              <div className="prose prose-sm max-w-none p-4 border rounded-md bg-gray-50 h-96 overflow-y-auto">
                <p>{selectedTemplate.content}</p>
                {/* A real implementation would render the full document here */}
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
              </div>
            </div>
          ) : (
            <div className="text-center py-20">
              <p className="text-gray-500">Select a template to preview</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Templates;
