import React, { createContext, useContext, useEffect, useState } from 'react';
import { supabase } from '../lib/supabase';

const AuthContext = createContext({});

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userProfile, setUserProfile] = useState(null);
  const [organizations, setOrganizations] = useState([]);
  const [currentOrganization, setCurrentOrganization] = useState(null);
  const [loading, setLoading] = useState(true);

  // Get user profile
  const getUserProfile = async (userId) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('id', userId)
        .single();

      if (error) throw error;
      return data;
    } catch (error) {
      console.error('Error fetching user profile:', error);
      return null;
    }
  };

  // Get user organizations
  const getUserOrganizations = async () => {
    try {
      const { data, error } = await supabase
        .rpc('get_user_organizations');

      if (error) {
        console.error('Error calling get_user_organizations RPC:', error);

        // Fallback: direct query if RPC function doesn't exist
        console.log('Trying fallback query...');
        const { data: fallbackData, error: fallbackError } = await supabase
          .from('user_organizations')
          .select(`
            organization_id,
            role,
            is_active,
            organizations (
              id,
              name,
              slug
            )
          `)
          .eq('user_id', (await supabase.auth.getUser()).data.user?.id)
          .eq('is_active', true);

        if (fallbackError) {
          console.error('Fallback query also failed:', fallbackError);
          return [];
        }

        // Transform fallback data to match expected format
        return fallbackData.map(item => ({
          organization_id: item.organization_id,
          organization_name: item.organizations?.name,
          organization_slug: item.organizations?.slug,
          role: item.role,
          is_active: item.is_active
        }));
      }

      return data || [];
    } catch (error) {
      console.error('Error fetching user organizations:', error);
      return [];
    }
  };

  // Sign up
  const signUp = async ({ email, password, firstName, lastName }) => {
    try {
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            first_name: firstName,
            last_name: lastName,
          },
        },
      });

      if (error) throw error;
      return { data, error: null };
    } catch (error) {
      return { data: null, error };
    }
  };

  // Sign in
  const signIn = async ({ email, password }) => {
    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) throw error;
      return { data, error: null };
    } catch (error) {
      return { data: null, error };
    }
  };

  // Sign out
  const signOut = async () => {
    try {
      const { error } = await supabase.auth.signOut();
      if (error) throw error;

      setUser(null);
      setUserProfile(null);
      setOrganizations([]);
      setCurrentOrganization(null);
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  // Create organization
  const createOrganization = async (organizationData) => {
    try {
      console.log('Creating organization with data:', organizationData);

      // Use the database function to create organization and add user as owner
      const { data, error } = await supabase.rpc('create_organization_with_owner', {
        org_name: organizationData.name,
        org_slug: organizationData.slug,
        org_description: organizationData.description,
        org_industry: organizationData.industry,
        org_website: organizationData.website
      });

      console.log('RPC call result:', { data, error });

      if (error) {
        console.error('Database error creating organization:', error);

        // If the RPC function doesn't exist, try manual approach
        if (error.message.includes('function') || error.message.includes('does not exist')) {
          console.log('RPC function not found, trying manual approach...');
          return await createOrganizationManual(organizationData);
        }

        throw error;
      }

      console.log('Organization created successfully:', data);

      // Refresh organizations
      const userOrgs = await getUserOrganizations();
      setOrganizations(userOrgs);

      // Set the new organization as current
      if (data && data.length > 0) {
        const newOrg = data[0];
        setCurrentOrganization({
          organization_id: newOrg.organization_id,
          organization_name: newOrg.organization_name,
          organization_slug: newOrg.organization_slug,
          role: newOrg.role
        });
      }

      return { data: data?.[0] || null, error: null };
    } catch (error) {
      console.error('Error creating organization:', error);
      return { data: null, error };
    }
  };

  // Manual organization creation fallback
  const createOrganizationManual = async (organizationData) => {
    try {
      console.log('Creating organization manually...');

      // Create organization
      const { data: org, error: orgError } = await supabase
        .from('organizations')
        .insert([{
          name: organizationData.name,
          slug: organizationData.slug,
          description: organizationData.description,
          industry: organizationData.industry,
          website: organizationData.website
        }])
        .select()
        .single();

      console.log('Organization insert result:', { org, orgError });

      if (orgError) {
        console.error('Error inserting organization:', orgError);
        throw orgError;
      }

      // Add user as owner
      const { error: userOrgError } = await supabase
        .from('user_organizations')
        .insert([{
          user_id: user.id,
          organization_id: org.id,
          role: 'owner',
        }]);

      console.log('User organization insert result:', { userOrgError });

      if (userOrgError) {
        console.error('Error inserting user organization:', userOrgError);
        throw userOrgError;
      }

      // Refresh organizations
      const userOrgs = await getUserOrganizations();
      setOrganizations(userOrgs);

      // Set the new organization as current
      setCurrentOrganization({
        organization_id: org.id,
        organization_name: org.name,
        organization_slug: org.slug,
        role: 'owner'
      });

      return { data: org, error: null };
    } catch (error) {
      console.error('Error in manual organization creation:', error);
      return { data: null, error };
    }
  };

  // Join organization
  const joinOrganization = async (invitationToken) => {
    try {
      // Get invitation details
      const { data: invitation, error: inviteError } = await supabase
        .from('organization_invitations')
        .select('*')
        .eq('token', invitationToken)
        .eq('accepted_at', null)
        .gt('expires_at', new Date().toISOString())
        .single();

      if (inviteError) throw inviteError;

      // Add user to organization
      const { error: userOrgError } = await supabase
        .from('user_organizations')
        .insert([{
          user_id: user.id,
          organization_id: invitation.organization_id,
          role: invitation.role,
        }]);

      if (userOrgError) throw userOrgError;

      // Mark invitation as accepted
      const { error: updateError } = await supabase
        .from('organization_invitations')
        .update({ accepted_at: new Date().toISOString() })
        .eq('id', invitation.id);

      if (updateError) throw updateError;

      // Refresh organizations
      const userOrgs = await getUserOrganizations();
      setOrganizations(userOrgs);

      return { data: invitation, error: null };
    } catch (error) {
      return { data: null, error };
    }
  };

  // Update user profile
  const updateUserProfile = async (updates) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .update(updates)
        .eq('id', user.id)
        .select()
        .single();

      if (error) throw error;
      setUserProfile(data);
      return { data, error: null };
    } catch (error) {
      return { data: null, error };
    }
  };

  // Check permissions
  const hasPermission = async (organizationId, requiredRole) => {
    try {
      const { data, error } = await supabase
        .rpc('has_permission', {
          org_id: organizationId,
          required_role: requiredRole,
        });

      if (error) throw error;
      return data;
    } catch (error) {
      console.error('Error checking permissions:', error);
      return false;
    }
  };

  // Initialize auth state
  useEffect(() => {
    const initializeAuth = async () => {
      try {
        console.log('Initializing auth...');

        // Get current session
        const { data: { session }, error: sessionError } = await supabase.auth.getSession();

        if (sessionError) {
          console.error('Session error:', sessionError);
        }

        if (session?.user) {
          console.log('User found:', session.user.email);
          setUser(session.user);

          try {
            // Get user profile
            const profile = await getUserProfile(session.user.id);
            setUserProfile(profile);
            console.log('User profile loaded:', profile);
          } catch (profileError) {
            console.error('Error loading user profile:', profileError);
          }

          try {
            // Get user organizations
            const userOrgs = await getUserOrganizations();
            setOrganizations(userOrgs);
            console.log('User organizations loaded:', userOrgs);

            // Set first organization as current
            if (userOrgs && userOrgs.length > 0) {
              setCurrentOrganization(userOrgs[0]);
              console.log('Set current organization:', userOrgs[0]);
            }
          } catch (orgError) {
            console.error('Error loading user organizations:', orgError);
            setOrganizations([]);
          }
        } else {
          console.log('No user session found');
        }
      } catch (error) {
        console.error('Error initializing auth:', error);
      } finally {
        console.log('Auth initialization complete, setting loading to false');
        setLoading(false);
      }
    };

    // Add timeout to prevent infinite loading
    const timeoutId = setTimeout(() => {
      console.log('Auth initialization timeout, forcing loading to false');
      setLoading(false);
    }, 10000); // 10 second timeout

    initializeAuth().finally(() => {
      clearTimeout(timeoutId);
    });

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        console.log('Auth state changed:', event, session?.user?.email);

        if (session?.user) {
          setUser(session.user);

          try {
            const profile = await getUserProfile(session.user.id);
            setUserProfile(profile);
          } catch (error) {
            console.error('Error loading profile on auth change:', error);
          }

          try {
            const userOrgs = await getUserOrganizations();
            setOrganizations(userOrgs);

            if (userOrgs && userOrgs.length > 0) {
              setCurrentOrganization(userOrgs[0]);
            }
          } catch (error) {
            console.error('Error loading organizations on auth change:', error);
            setOrganizations([]);
          }
        } else {
          setUser(null);
          setUserProfile(null);
          setOrganizations([]);
          setCurrentOrganization(null);
        }
        setLoading(false);
      }
    );

    return () => {
      clearTimeout(timeoutId);
      subscription.unsubscribe();
    };
  }, []);

  const value = {
    user,
    userProfile,
    organizations,
    currentOrganization,
    loading,
    signUp,
    signIn,
    signOut,
    createOrganization,
    joinOrganization,
    updateUserProfile,
    hasPermission,
    setCurrentOrganization,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
