import React, { useState } from 'react';
import SubNav from '../../components/common/layout/SubNav';
import SalesDashboard from './components/SalesDashboard';
import CustomerManagement from './components/CustomerManagement';
import PipelineManagement from './components/PipelineManagement';
import SalesReports from './components/SalesReports';

const Sales = () => {
  const [activeSubTab, setActiveSubTab] = useState('dashboard');

  const subTabs = [
    { id: 'dashboard', name: 'Sales Dashboard' },
    { id: 'customers', name: 'Customer Management' },
    { id: 'pipeline', name: 'Pipeline' },
    { id: 'reports', name: 'Reports' },
  ];

  const renderContent = () => {
    switch (activeSubTab) {
      case 'dashboard':
        return <SalesDashboard />;
      case 'customers':
        return <CustomerManagement />;
      case 'pipeline':
        return <PipelineManagement />;
      case 'reports':
        return <SalesReports />;
      default:
        return <SalesDashboard />;
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Sales CRM</h1>
          <p className="text-gray-600">Manage your sales pipeline, customers, and track performance.</p>
        </div>
      </div>

      <SubNav
        subTabs={subTabs}
        activeSubTab={activeSubTab}
        onSubTabChange={setActiveSubTab}
      />

      <div className="mt-8">
        {renderContent()}
      </div>
    </div>
  );
};

export default Sales;
