import React, { useState, useEffect } from 'react';
import { PlusIcon, ChevronDownIcon, ChevronUpIcon, PencilIcon, TrashIcon, XMarkIcon } from '@heroicons/react/20/solid';
import { useCapTable } from '../../../../hooks/useCapTable';
import { capTableService } from '../../../../services/capTableService';

const formatCurrency = (value) => `Â£${(value / 1000000).toFixed(2)}M`;
const formatNumber = (value) => new Intl.NumberFormat('en-GB').format(value);
const formatPercent = (value) => `${value.toFixed(1)}%`;

const RoundHistory = () => {
  const {
    rounds,
    shareholders,
    shareClasses,
    transactions,
    addRound,
    updateRound,
    deleteRound,
    addTransaction,
    updateTransaction,
    deleteTransaction,
    addShareholder,
    loading,
    error
  } = useCapTable();

  const [expandedRounds, setExpandedRounds] = useState(new Set());
  const [editingRound, setEditingRound] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showNewShareholderForm, setShowNewShareholderForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    date: new Date().toISOString().split('T')[0],
    pre_money_valuation: 0,
    share_class_id: '',
    option_pool_target: 0,
  });
  const [participants, setParticipants] = useState([]);
  const [newParticipant, setNewParticipant] = useState({
    shareholder_id: '',
    investment_amount: 0,
    shares_issued: 0,
    preference_multiplier: 0
  });
  const [newShareholder, setNewShareholder] = useState({
    name: '',
    role: 'Investor',
    email: ''
  });

  // Debug: Monitor shareholders changes
  useEffect(() => {
    console.log('Shareholders list updated:', shareholders);
  }, [shareholders]);

  // Test function to directly test the service
  const testAddShareholder = async () => {
    try {
      console.log('Testing direct service call...');
      const testShareholder = {
        name: 'Test Shareholder ' + Date.now(),
        role: 'Investor',
        email: 'test@example.com'
      };
      console.log('Test shareholder data:', testShareholder);

      const result = await capTableService.createShareholder(testShareholder);
      console.log('Direct service call result:', result);

      // Try to refresh the shareholders list
      console.log('Current shareholders before refresh:', shareholders);
      // Force a reload of the data
      window.location.reload();
    } catch (err) {
      console.error('Direct service call error:', err);
      alert(`Test failed: ${err.message}`);
    }
  };

  const toggleRound = (roundId) => {
    const newExpanded = new Set(expandedRounds);
    if (newExpanded.has(roundId)) {
      newExpanded.delete(roundId);
    } else {
      newExpanded.add(roundId);
    }
    setExpandedRounds(newExpanded);
  };

  const handleAddNewShareholder = async (e) => {
    e.preventDefault();
    try {
      console.log('Adding new shareholder:', newShareholder);
      const createdShareholder = await addShareholder(newShareholder);
      console.log('Created shareholder:', createdShareholder);

      // Update the newParticipant with the new shareholder ID
      setNewParticipant({ ...newParticipant, shareholder_id: createdShareholder.id });

      // Reset the newShareholder form
      setNewShareholder({ name: '', role: 'Investor', email: '' });

      // Close the modal
      setShowNewShareholderForm(false);

      console.log('Shareholder added successfully, updated newParticipant:', { ...newParticipant, shareholder_id: createdShareholder.id });
    } catch (err) {
      console.error('Error adding new shareholder:', err);
      alert(`Failed to add shareholder: ${err.message}`);
    }
  };

  const addParticipant = () => {
    if (newParticipant.shareholder_id && newParticipant.shares_issued > 0) {
      setParticipants([...participants, { ...newParticipant, id: Date.now() }]);
      setNewParticipant({
        shareholder_id: '',
        investment_amount: 0,
        shares_issued: 0,
        preference_multiplier: 0
      });
    }
  };

  const removeParticipant = (participantId) => {
    setParticipants(participants.filter(p => p.id !== participantId));
  };

  const calculateTotalInvestment = (participantsList) => {
    return participantsList.reduce((sum, p) => sum + (p.investment_amount || 0), 0);
  };

  const handleAddRound = async (e) => {
    e.preventDefault();
    try {
      // Calculate total investment from participants
      const totalInvestment = calculateTotalInvestment(participants);

      const roundData = {
        name: formData.name,
        date: formData.date,
        pre_money_valuation: formData.pre_money_valuation,
        investment_amount: totalInvestment,
        share_class_id: formData.share_class_id,
      };

      const newRound = await addRound(roundData, formData.option_pool_target, participants);

      // The addRound function in the hook now handles all transaction creation

      setShowAddForm(false);
      setFormData({
        name: '',
        date: new Date().toISOString().split('T')[0],
        pre_money_valuation: 0,
        share_class_id: '',
        option_pool_target: 0,
      });
      setParticipants([]);
      setExpandedRounds(new Set([newRound.round.id]));
    } catch (err) {
      console.error('Error adding round:', err);
      // Display a more helpful error message to the user
      alert(`Error adding round: ${err.message}`);
    }
  };

  const handleEditRound = async (roundId, updates) => {
    try {
      await updateRound(roundId, updates);
      setEditingRound(null);
    } catch (err) {
      console.error('Error updating round:', err);
    }
  };

  const handleDeleteRound = async (roundId) => {
    if (window.confirm('Are you sure you want to delete this round? This will also delete all associated transactions.')) {
      try {
        // Delete associated transactions first
        const roundTransactions = transactions.filter(t => t.round_id === roundId);
        for (const transaction of roundTransactions) {
          await deleteTransaction(transaction.id);
        }
        // Delete the round
        await deleteRound(roundId);
      } catch (err) {
        console.error('Error deleting round:', err);
      }
    }
  };

  const getRoundParticipants = (roundId) => {
    return transactions.filter(t => t.round_id === roundId);
  };

  const calculatePostMoney = (round) => {
    const roundParticipants = getRoundParticipants(round.id);
    const totalInvestment = calculateTotalInvestment(roundParticipants);
    return round.pre_money_valuation + totalInvestment;
  };

  const calculateRoundInvestment = (roundId) => {
    const roundParticipants = getRoundParticipants(roundId);
    return calculateTotalInvestment(roundParticipants);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading round history...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <div className="flex">
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error loading round history</h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with Add Button */}
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-bold text-gray-900">Round History</h2>
        <button
          onClick={() => setShowAddForm(true)}
          className="flex items-center space-x-2 px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700 transition-colors"
        >
          <PlusIcon className="h-4 w-4" />
          <span>Add Round</span>
        </button>
      </div>

      {/* Add Round Form */}
      {showAddForm && (
        <div className="bg-white rounded-lg shadow border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Add New Round</h3>
          <form onSubmit={handleAddRound} className="space-y-6">
            {/* Round Details */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Round Name</label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
                <input
                  type="date"
                  value={formData.date}
                  onChange={(e) => setFormData({...formData, date: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Pre-money Valuation (Â£)</label>
                <input
                  type="number"
                  value={formData.pre_money_valuation}
                  onChange={(e) => setFormData({...formData, pre_money_valuation: parseFloat(e.target.value) || 0})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Option Pool Target (%)</label>
                <input
                  type="number"
                  min="0"
                  max="100"
                  step="0.1"
                  value={formData.option_pool_target}
                  onChange={(e) => setFormData({...formData, option_pool_target: parseFloat(e.target.value) || 0})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  placeholder="e.g. 10"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Share Class</label>
              <select
                value={formData.share_class_id}
                onChange={(e) => setFormData({...formData, share_class_id: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                required
              >
                <option value="">Select Share Class</option>
                {shareClasses.map(shareClass => (
                  <option key={shareClass.id} value={shareClass.id}>{shareClass.name}</option>
                ))}
              </select>
            </div>

            {/* Participants Section */}
            <div className="border-t pt-6">
              <h4 className="text-md font-medium text-gray-900 mb-4">Round Participants</h4>

              {/* Add Participant Form */}
              <div className="bg-gray-50 p-4 rounded-lg mb-4">
                <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Shareholder</label>
                    <select
                      value={newParticipant.shareholder_id}
                      onChange={(e) => {
                        console.log('Shareholder dropdown changed:', e.target.value);
                        if (e.target.value === 'new') {
                          setShowNewShareholderForm(true);
                        } else {
                          setNewParticipant({...newParticipant, shareholder_id: e.target.value});
                        }
                      }}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500 bg-white shadow-sm"
                    >
                      <option value="">Select Shareholder</option>
                      {shareholders.map(shareholder => (
                        <option key={shareholder.id} value={shareholder.id}>{shareholder.name}</option>
                      ))}
                      <option value="new" className="font-semibold text-purple-600 bg-purple-50">+ Add New Shareholder</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Investment (Â£)</label>
                    <input
                      type="number"
                      value={newParticipant.investment_amount}
                      onChange={(e) => setNewParticipant({...newParticipant, investment_amount: parseFloat(e.target.value) || 0})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Shares</label>
                    <input
                      type="number"
                      value={newParticipant.shares_issued}
                      onChange={(e) => setNewParticipant({...newParticipant, shares_issued: parseFloat(e.target.value) || 0})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Preference Multiplier</label>
                    <input
                      type="number"
                      step="0.1"
                      min="0"
                      value={newParticipant.preference_multiplier}
                      onChange={(e) => setNewParticipant({...newParticipant, preference_multiplier: parseFloat(e.target.value) || 1})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                      placeholder="1.0"
                    />
                  </div>
                  <div className="flex items-end">
                    <button
                      type="button"
                      onClick={addParticipant}
                      className="w-full px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700"
                    >
                      Add Participant
                    </button>
                  </div>
                </div>
              </div>

              {/* Participants List */}
              {participants.length > 0 && (
                <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Shareholder</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Investment</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Shares</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Preference Multiplier</th>
                        <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Action</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {participants.map((participant) => {
                        const shareholder = shareholders.find(sh => sh.id === participant.shareholder_id);
                        return (
                          <tr key={participant.id}>
                            <td className="px-4 py-3 text-sm font-medium text-gray-900">
                              {shareholder?.name || 'Unknown'}
                            </td>
                            <td className="px-4 py-3 text-sm text-gray-700">
                              {formatCurrency(participant.investment_amount)}
                            </td>
                            <td className="px-4 py-3 text-sm text-gray-700">
                              {formatNumber(participant.shares_issued)}
                            </td>
                            <td className="px-4 py-3 text-sm text-gray-700">
                              {participant.preference_multiplier}x
                            </td>
                            <td className="px-4 py-3 text-sm text-gray-700">
                              <button
                                type="button"
                                onClick={() => removeParticipant(participant.id)}
                                className="text-red-600 hover:text-red-800"
                              >
                                <XMarkIcon className="h-4 w-4" />
                              </button>
                            </td>
                          </tr>
                        );
                      })}
                    </tbody>
                  </table>
                </div>
              )}

              {/* Total Investment Display */}
              {participants.length > 0 && (
                <div className="mt-4 p-3 bg-purple-50 rounded-lg">
                  <p className="text-sm font-medium text-purple-900">
                    Total Investment: {formatCurrency(calculateTotalInvestment(participants))}
                  </p>
                </div>
              )}
            </div>

            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => {
                  setShowAddForm(false);
                  setParticipants([]);
                }}
                className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={participants.length === 0}
                className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700 disabled:bg-gray-300 disabled:cursor-not-allowed"
              >
                Add Round
              </button>
            </div>
          </form>
        </div>
      )}

      {/* New Shareholder Form Modal - Moved outside the main form */}
      {showNewShareholderForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4">
            <h4 className="text-lg font-semibold text-gray-900 mb-4">Add New Shareholder</h4>
            <form onSubmit={(e) => {
              console.log('Modal form submitted');
              console.log('Current newShareholder state:', newShareholder);
              handleAddNewShareholder(e);
            }} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                <input
                  type="text"
                  value={newShareholder.name}
                  onChange={(e) => {
                    console.log('Name changed to:', e.target.value);
                    setNewShareholder({...newShareholder, name: e.target.value});
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Role</label>
                <select
                  value={newShareholder.role}
                  onChange={(e) => {
                    console.log('Role changed to:', e.target.value);
                    setNewShareholder({...newShareholder, role: e.target.value});
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                >
                  <option value="Investor">Investor</option>
                  <option value="Founder">Founder</option>
                  <option value="Employee">Employee</option>
                  <option value="Advisor">Advisor</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email (Optional)</label>
                <input
                  type="email"
                  value={newShareholder.email}
                  onChange={(e) => {
                    console.log('Email changed to:', e.target.value);
                    setNewShareholder({...newShareholder, email: e.target.value});
                  }}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
              </div>
              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={() => setShowNewShareholderForm(false)}
                  className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700"
                >
                  Add Shareholder
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Rounds List */}
      <div className="space-y-4">
        {rounds.map((round) => (
          <div key={round.id} className="bg-white rounded-lg shadow border border-gray-200">
            {/* Round Header */}
            <div 
              className="p-6 cursor-pointer hover:bg-gray-50 transition-colors"
              onClick={() => toggleRound(round.id)}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <div className="w-48">
                    <h3 className="text-lg font-semibold text-gray-900 truncate">{round.name}</h3>
                    <p className="text-sm text-gray-500">{round.date}</p>
                  </div>
                  <div className="flex items-center space-x-6">
                    <div className="text-center">
                      <p className="text-xs text-gray-500">Pre-money</p>
                      <p className="font-semibold text-gray-900">{formatCurrency(round.pre_money_valuation)}</p>
                    </div>
                    <div className="text-center">
                      <p className="text-xs text-gray-500">Investment</p>
                      <p className="font-semibold text-purple-600">{formatCurrency(calculateRoundInvestment(round.id))}</p>
                    </div>
                    <div className="text-center">
                      <p className="text-xs text-gray-500">Post-money</p>
                      <p className="font-semibold text-gray-900">{formatCurrency(calculatePostMoney(round))}</p>
                    </div>
                    <div className="text-left">
                      <p className="text-xs text-gray-500">Share Class</p>
                      <p className="font-semibold text-gray-900">
                        {shareClasses.find(sc => sc.id === round.share_class_id)?.name || 'Unknown'}
                      </p>
                    </div>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-gray-500">
                    {getRoundParticipants(round.id).length} participants
                  </span>
                  <div className="flex space-x-1">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        setEditingRound(round.id);
                      }}
                      className="p-1 text-gray-400 hover:text-gray-600"
                    >
                      <PencilIcon className="h-4 w-4" />
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteRound(round.id);
                      }}
                      className="p-1 text-gray-400 hover:text-red-600"
                    >
                      <TrashIcon className="h-4 w-4" />
                    </button>
                  </div>
                  {expandedRounds.has(round.id) ? (
                    <ChevronUpIcon className="h-5 w-5 text-gray-400" />
                  ) : (
                    <ChevronDownIcon className="h-5 w-5 text-gray-400" />
                  )}
                </div>
              </div>
            </div>

            {/* Expanded Content */}
            {expandedRounds.has(round.id) && (
              <div className="border-t border-gray-200 bg-gray-50">
                <div className="p-6">
                  <h4 className="text-sm font-medium text-gray-900 mb-4">Round Participants</h4>
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-white">
                        <tr>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Participant</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Investment</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Shares</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ownership %</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {getRoundParticipants(round.id).map((transaction) => {
                          const shareholder = shareholders.find(sh => sh.id === transaction.shareholder_id);
                          const postMoney = calculatePostMoney(round);
                          return (
                            <tr key={transaction.id}>
                              <td className="px-4 py-3 whitespace-nowrap text-sm font-medium text-gray-900">
                                {shareholder?.name || 'Unknown'}
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-500">
                                <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                  shareholder?.role === 'Founder' ? 'bg-green-100 text-green-800' :
                                  shareholder?.role === 'Investor' ? 'bg-blue-100 text-blue-800' :
                                  'bg-gray-100 text-gray-800'
                                }`}>
                                  {shareholder?.role || 'Unknown'}
                                </span>
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-700">
                                {transaction.investment_amount > 0 ? formatCurrency(transaction.investment_amount) : '-'}
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-700">
                                {formatNumber(transaction.shares_issued)}
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-700">
                                {formatPercent((transaction.shares_issued / postMoney) * 100)}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoundHistory;
