import React, { useState } from 'react';
import { PlusIcon, ChevronDownIcon, ChevronUpIcon, PencilIcon, TrashIcon } from '@heroicons/react/20/solid';
import { useCapTable } from '../../../../hooks/useCapTable';

const formatCurrency = (value) => `Â£${(value / 1000000).toFixed(2)}M`;
const formatNumber = (value) => new Intl.NumberFormat('en-GB').format(value);
const formatPercent = (value) => `${value.toFixed(1)}%`;

const RoundHistory = () => {
  const {
    rounds,
    shareholders,
    shareClasses,
    transactions,
    addRound,
    updateRound,
    deleteRound,
    addTransaction,
    updateTransaction,
    deleteTransaction,
    loading,
    error
  } = useCapTable();

  const [expandedRounds, setExpandedRounds] = useState(new Set());
  const [editingRound, setEditingRound] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    date: new Date().toISOString().split('T')[0],
    pre_money_valuation: 0,
    investment_amount: 0,
    share_class_id: ''
  });

  const toggleRound = (roundId) => {
    const newExpanded = new Set(expandedRounds);
    if (newExpanded.has(roundId)) {
      newExpanded.delete(roundId);
    } else {
      newExpanded.add(roundId);
    }
    setExpandedRounds(newExpanded);
  };

  const handleAddRound = async (e) => {
    e.preventDefault();
    try {
      const newRound = await addRound(formData);
      setShowAddForm(false);
      setFormData({
        name: '',
        date: new Date().toISOString().split('T')[0],
        pre_money_valuation: 0,
        investment_amount: 0,
        share_class_id: ''
      });
      setExpandedRounds(new Set([newRound.id]));
    } catch (err) {
      console.error('Error adding round:', err);
    }
  };

  const handleEditRound = async (roundId, updates) => {
    try {
      await updateRound(roundId, updates);
      setEditingRound(null);
    } catch (err) {
      console.error('Error updating round:', err);
    }
  };

  const handleDeleteRound = async (roundId) => {
    if (window.confirm('Are you sure you want to delete this round? This will also delete all associated transactions.')) {
      try {
        // Delete associated transactions first
        const roundTransactions = transactions.filter(t => t.round_id === roundId);
        for (const transaction of roundTransactions) {
          await deleteTransaction(transaction.id);
        }
        // Delete the round
        await deleteRound(roundId);
      } catch (err) {
        console.error('Error deleting round:', err);
      }
    }
  };

  const getRoundParticipants = (roundId) => {
    return transactions.filter(t => t.round_id === roundId);
  };

  const calculatePostMoney = (round) => {
    return round.pre_money_valuation + round.investment_amount;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading round history...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <div className="flex">
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">Error loading round history</h3>
            <div className="mt-2 text-sm text-red-700">
              <p>{error}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with Add Button */}
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-bold text-gray-900">Round History</h2>
        <button
          onClick={() => setShowAddForm(true)}
          className="flex items-center space-x-2 px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700 transition-colors"
        >
          <PlusIcon className="h-4 w-4" />
          <span>Add Round</span>
        </button>
      </div>

      {/* Add Round Form */}
      {showAddForm && (
        <div className="bg-white rounded-lg shadow border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Add New Round</h3>
          <form onSubmit={handleAddRound} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Round Name</label>
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
                <input
                  type="date"
                  value={formData.date}
                  onChange={(e) => setFormData({...formData, date: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Pre-money Valuation (Â£)</label>
                <input
                  type="number"
                  value={formData.pre_money_valuation}
                  onChange={(e) => setFormData({...formData, pre_money_valuation: parseFloat(e.target.value) || 0})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Investment Amount (Â£)</label>
                <input
                  type="number"
                  value={formData.investment_amount}
                  onChange={(e) => setFormData({...formData, investment_amount: parseFloat(e.target.value) || 0})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Share Class</label>
                <select
                  value={formData.share_class_id}
                  onChange={(e) => setFormData({...formData, share_class_id: e.target.value})}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  required
                >
                  <option value="">Select Share Class</option>
                  {shareClasses.map(shareClass => (
                    <option key={shareClass.id} value={shareClass.id}>{shareClass.name}</option>
                  ))}
                </select>
              </div>
            </div>
            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => setShowAddForm(false)}
                className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="px-4 py-2 bg-purple-600 text-white rounded-md hover:bg-purple-700"
              >
                Add Round
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Rounds List */}
      <div className="space-y-4">
        {rounds.map((round) => (
          <div key={round.id} className="bg-white rounded-lg shadow border border-gray-200">
            {/* Round Header */}
            <div 
              className="p-6 cursor-pointer hover:bg-gray-50 transition-colors"
              onClick={() => toggleRound(round.id)}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900">{round.name}</h3>
                    <p className="text-sm text-gray-500">{round.date}</p>
                  </div>
                  <div className="flex items-center space-x-6">
                    <div className="text-center">
                      <p className="text-xs text-gray-500">Pre-money</p>
                      <p className="font-semibold text-gray-900">{formatCurrency(round.pre_money_valuation)}</p>
                    </div>
                    <div className="text-center">
                      <p className="text-xs text-gray-500">Investment</p>
                      <p className="font-semibold text-purple-600">{formatCurrency(round.investment_amount)}</p>
                    </div>
                    <div className="text-center">
                      <p className="text-xs text-gray-500">Post-money</p>
                      <p className="font-semibold text-gray-900">{formatCurrency(calculatePostMoney(round))}</p>
                    </div>
                    <div className="text-left">
                      <p className="text-xs text-gray-500">Share Class</p>
                      <p className="font-semibold text-gray-900">
                        {shareClasses.find(sc => sc.id === round.share_class_id)?.name || 'Unknown'}
                      </p>
                    </div>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-gray-500">
                    {getRoundParticipants(round.id).length} participants
                  </span>
                  <div className="flex space-x-1">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        setEditingRound(round.id);
                      }}
                      className="p-1 text-gray-400 hover:text-gray-600"
                    >
                      <PencilIcon className="h-4 w-4" />
                    </button>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteRound(round.id);
                      }}
                      className="p-1 text-gray-400 hover:text-red-600"
                    >
                      <TrashIcon className="h-4 w-4" />
                    </button>
                  </div>
                  {expandedRounds.has(round.id) ? (
                    <ChevronUpIcon className="h-5 w-5 text-gray-400" />
                  ) : (
                    <ChevronDownIcon className="h-5 w-5 text-gray-400" />
                  )}
                </div>
              </div>
            </div>

            {/* Expanded Content */}
            {expandedRounds.has(round.id) && (
              <div className="border-t border-gray-200 bg-gray-50">
                <div className="p-6">
                  <h4 className="text-sm font-medium text-gray-900 mb-4">Round Participants</h4>
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-white">
                        <tr>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Participant</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Investment</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Shares</th>
                          <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ownership %</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {getRoundParticipants(round.id).map((transaction) => {
                          const shareholder = shareholders.find(sh => sh.id === transaction.shareholder_id);
                          return (
                            <tr key={transaction.id}>
                              <td className="px-4 py-3 whitespace-nowrap text-sm font-medium text-gray-900">
                                {shareholder?.name || 'Unknown'}
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-500">
                                <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                                  shareholder?.role === 'Founder' ? 'bg-green-100 text-green-800' :
                                  shareholder?.role === 'Investor' ? 'bg-blue-100 text-blue-800' :
                                  'bg-gray-100 text-gray-800'
                                }`}>
                                  {shareholder?.role || 'Unknown'}
                                </span>
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-700">
                                {transaction.investment_amount > 0 ? formatCurrency(transaction.investment_amount) : '-'}
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-700">
                                {formatNumber(transaction.shares_issued)}
                              </td>
                              <td className="px-4 py-3 whitespace-nowrap text-sm text-gray-700">
                                {formatPercent((transaction.shares_issued / (round.pre_money_valuation + round.investment_amount)) * 100)}
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoundHistory;
