import React, { useState } from 'react';
import { DocumentMagnifyingGlassIcon, BuildingStorefrontIcon, MapPinIcon, GlobeAltIcon } from '@heroicons/react/20/solid';

const mockListings = [
  { 
    id: 1,
    name: 'Innovate Inc.', 
    field: 'Technology & AI',
    description: 'Cutting-edge AI solutions for startups and scale-ups. We specialize in machine learning, natural language processing, and predictive analytics to help businesses leverage the power of artificial intelligence.',
    category: 'Tech', 
    logo: 'https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg',
    location: 'San Francisco, CA',
    website: 'www.innovateinc.com',
    rating: 4.8,
    reviews: 127
  },
  { 
    id: 2,
    name: 'Design Co.', 
    field: 'Creative Design',
    description: 'Creative branding and design services that help businesses stand out. From logo design to complete brand identity systems, we create memorable visual experiences that drive engagement and growth.',
    category: 'Design', 
    logo: 'https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg',
    location: 'New York, NY',
    website: 'www.designco.com',
    rating: 4.9,
    reviews: 89
  },
  { 
    id: 3,
    name: 'Legal Eagles', 
    field: 'Legal Services',
    description: 'Affordable legal services for startups and small businesses. Our team of experienced attorneys specializes in corporate law, intellectual property, employment law, and regulatory compliance.',
    category: 'Legal', 
    logo: 'https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg',
    location: 'Austin, TX',
    website: 'www.legaleagles.com',
    rating: 4.7,
    reviews: 156
  },
  { 
    id: 4,
    name: 'Number Crunchers', 
    field: 'Financial Services',
    description: 'Accounting and financial consulting for growing businesses. We provide bookkeeping, tax preparation, financial planning, and CFO services to help you make informed business decisions.',
    category: 'Finance', 
    logo: 'https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg',
    location: 'Chicago, IL',
    website: 'www.numbercrunchers.com',
    rating: 4.6,
    reviews: 203
  },
  { 
    id: 5,
    name: 'Growth Hackers', 
    field: 'Digital Marketing',
    description: 'Marketing services for fast growth. We specialize in digital marketing, SEO, content strategy, and growth hacking techniques to help businesses scale rapidly and efficiently.',
    category: 'Marketing',
    location: 'Los Angeles, CA',
    website: 'www.growthhackers.com',
    rating: 4.8,
    reviews: 94
  },
  { 
    id: 6,
    name: 'Cloud Solutions Pro', 
    field: 'Cloud Infrastructure',
    description: 'Cloud infrastructure and DevOps services for modern businesses. We help companies migrate to the cloud, optimize their infrastructure, and implement CI/CD pipelines for faster development cycles.',
    category: 'Tech',
    location: 'Seattle, WA',
    website: 'www.cloudsolutionspro.com',
    rating: 4.9,
    reviews: 67
  }
];

const ListingCard = ({ listing }) => (
  <div className="bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200 overflow-hidden">
    {/* Header with logo and basic info */}
    <div className="p-6 border-b border-gray-100">
      <div className="flex items-start justify-between">
        <div className="flex items-center space-x-4">
          <div className="flex-shrink-0 h-16 w-16 bg-gray-100 rounded-lg flex items-center justify-center">
            {listing.logo ? (
              <img src={listing.logo} alt={`${listing.name} logo`} className="h-12 w-12 object-contain" />
            ) : (
              <BuildingStorefrontIcon className="h-8 w-8 text-gray-400" />
            )}
          </div>
          <div>
            <h3 className="text-lg font-semibold text-gray-900">{listing.name}</h3>
            <p className="text-sm font-medium text-blue-600">{listing.field}</p>
            <div className="flex items-center space-x-4 mt-1">
              <div className="flex items-center text-sm text-gray-500">
                <MapPinIcon className="h-4 w-4 mr-1" />
                {listing.location}
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <GlobeAltIcon className="h-4 w-4 mr-1" />
                {listing.website}
              </div>
            </div>
          </div>
        </div>
        <div className="text-right">
          <div className="flex items-center space-x-1">
            <span className="text-sm font-medium text-gray-900">{listing.rating}</span>
            <span className="text-yellow-400">â˜…</span>
            <span className="text-sm text-gray-500">({listing.reviews})</span>
          </div>
        </div>
      </div>
    </div>

    {/* Description */}
    <div className="p-6">
      <p className="text-gray-600 leading-relaxed">{listing.description}</p>
    </div>

    {/* Footer with actions */}
    <div className="px-6 py-4 bg-gray-50 border-t border-gray-100">
      <div className="flex items-center justify-between">
        <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
          {listing.category}
        </span>
        <div className="flex space-x-2">
          <button className="px-4 py-2 text-sm font-medium text-blue-600 hover:text-blue-700 hover:bg-blue-50 rounded-md transition-colors">
            View Profile
          </button>
          <button className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-md transition-colors">
            Contact
          </button>
        </div>
      </div>
    </div>
  </div>
);

const MarketplaceListings = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const categories = ['All', 'Tech', 'Design', 'Legal', 'Finance', 'Marketing'];

  const filteredListings = mockListings.filter(listing => {
    const matchesSearch = listing.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         listing.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         listing.field.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || listing.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  return (
    <div>
      {/* Search and Filters */}
      <div className="mb-6">
        <div className="relative">
          <input
            type="text"
            placeholder="Search businesses, services, or expertise..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-3 rounded-lg border border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
          <DocumentMagnifyingGlassIcon className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
        </div>
        <div className="mt-4 flex flex-wrap gap-2">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={`px-4 py-2 text-sm font-medium rounded-full transition-colors ${
                selectedCategory === category
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      {/* Results count */}
      <div className="mb-6">
        <p className="text-sm text-gray-600">
          Showing {filteredListings.length} of {mockListings.length} businesses
        </p>
      </div>

      {/* Listings Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredListings.map((listing) => (
          <ListingCard key={listing.id} listing={listing} />
        ))}
      </div>

      {filteredListings.length === 0 && (
        <div className="text-center py-12">
          <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-4">
            <DocumentMagnifyingGlassIcon className="w-8 h-8 text-gray-400" />
          </div>
          <p className="text-gray-500 text-sm">No businesses found matching your criteria.</p>
        </div>
      )}
    </div>
  );
};

export default MarketplaceListings;
