import React, { useState } from 'react';
import SubNav from '../../components/common/layout/SubNav';
import MarketingDashboard from './components/MarketingDashboard';
import ContentSchedule from './components/content/ContentSchedule';
import EventsCalendar from './components/events/EventsCalendar';
import BrandGuidelines from './components/brand/BrandGuidelines';

const Marketing = () => {
  const [activeSubTab, setActiveSubTab] = useState('dashboard');

  const subTabs = [
    { id: 'dashboard', name: 'Dashboard' },
    { id: 'content', name: 'Content Schedule' },
    { id: 'events', name: 'Events Calendar' },
    { id: 'brand', name: 'Brand Guidelines' },
  ];

  const renderContent = () => {
    switch (activeSubTab) {
      case 'dashboard':
        return <MarketingDashboard />;
      case 'content':
        return <ContentSchedule />;
      case 'events':
        return <EventsCalendar />;
      case 'brand':
        return <BrandGuidelines />;
      default:
        return <MarketingDashboard />;
    }
  };

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Marketing</h1>
      </div>
      <SubNav
        subTabs={subTabs}
        activeSubTab={activeSubTab}
        onSubTabChange={setActiveSubTab}
      />
      <div className="mt-8">
        {renderContent()}
      </div>
    </div>
  );
};

export default Marketing;
