import React, { useState } from 'react';
import Budget from './components/budget/Budget';

const tabs = [
  { id: 'overview', name: 'Overview' },
  { id: 'budget', name: 'Budget' },
  { id: 'invoicing', name: 'Invoicing' },
  { id: 'management', name: 'Management Accounts' },
];

const Finance = () => {
  const [activeTab, setActiveTab] = useState('overview');

  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-medium text-gray-900">Finance Overview</h3>
            <p className="text-gray-600">Month-on-month summary of budget, forecast, invoicing, and management accounts.</p>
          </div>
        );
      case 'budget':
        return <Budget />;
      case 'invoicing':
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-medium text-gray-900">Invoicing</h3>
            <p className="text-gray-600">Invoicing content coming soon.</p>
          </div>
        );
      case 'management':
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-medium text-gray-900">Management Accounts</h3>
            <p className="text-gray-600">Management accounts content coming soon.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex space-x-2 border-b border-gray-200 mb-4">
        {tabs.map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`px-4 py-2 text-sm font-medium border-b-2 transition-colors duration-150 ${
              activeTab === tab.id
                ? 'border-purple-600 text-purple-700 bg-white'
                : 'border-transparent text-gray-500 hover:text-purple-700 hover:bg-gray-50'
            }`}
          >
            {tab.name}
          </button>
        ))}
      </div>
      <div>
        {renderContent()}
      </div>
    </div>
  );
};

export default Finance;
