import React, { useState } from 'react';
import TopNav from './components/common/layout/TopNav';
import SecondaryNav from './components/common/layout/SecondaryNav';
import Dashboard from './pages/Dashboard/index';
import CompanySetup from './pages/company-setup';
import Finance from './pages/finance';
import CapTable from './pages/cap-table';
import Marketing from './pages/marketing';
import HR from './pages/hr';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');

  const tabs = [
    { id: 'dashboard', name: 'Dashboard' },
    { id: 'company', name: 'Company Setup' },
    { id: 'finance', name: 'Finance' },
    { id: 'captable', name: 'Cap Table' },
    { id: 'marketing', name: 'Marketing' },
    { id: 'hr', name: 'HR' },
  ];

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard />;
      case 'company':
        return <CompanySetup />;
      case 'finance':
        return <Finance />;
      case 'captable':
        return <CapTable />;
      case 'marketing':
        return <Marketing />;
      case 'hr':
        return <HR />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <TopNav />
      <SecondaryNav 
        tabs={tabs}
        activeTab={activeTab}
        onTabChange={setActiveTab}
      />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {renderContent()}
      </main>
    </div>
  );
}

export default App;
