import { useState, useEffect, useCallback } from 'react';
import { capTableService } from '../services/capTableService';

export const useCapTable = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [capTable, setCapTable] = useState(null);
  const [rounds, setRounds] = useState([]);
  const [shareholders, setShareholders] = useState([]);
  const [shareClasses, setShareClasses] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [preferenceTerms, setPreferenceTerms] = useState([]);

  // Load all data
  const loadData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const [
        shareholdersData,
        shareClassesData,
        roundsData,
        transactionsData,
        preferenceTermsData,
        capTableData
      ] = await Promise.all([
        capTableService.getShareholders(),
        capTableService.getShareClasses(),
        capTableService.getFinancingRounds(),
        capTableService.getTransactions(),
        capTableService.getPreferenceTerms(),
        capTableService.getCapTableAtRound()
      ]);

      setShareholders(shareholdersData);
      setShareClasses(shareClassesData);
      setRounds(roundsData);
      setTransactions(transactionsData);
      setPreferenceTerms(preferenceTermsData);
      setCapTable(capTableData);
    } catch (err) {
      setError(err.message);
      console.error('Error loading cap table data:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Load cap table at specific round
  const loadCapTableAtRound = useCallback(async (roundId) => {
    try {
      setLoading(true);
      const data = await capTableService.getCapTableAtRound(roundId);
      setCapTable(data);
    } catch (err) {
      setError(err.message);
      console.error('Error loading cap table at round:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Calculate exit scenario
  const calculateExitScenario = useCallback(async (acquisitionAmount, acquisitionPercentage, preferenceType) => {
    try {
      const result = await capTableService.calculateExitScenario(
        acquisitionAmount,
        acquisitionPercentage,
        preferenceType
      );
      return result;
    } catch (err) {
      setError(err.message);
      console.error('Error calculating exit scenario:', err);
      throw err;
    }
  }, []);

  // CRUD operations for shareholders
  const addShareholder = useCallback(async (shareholder) => {
    try {
      console.log('useCapTable: Adding shareholder:', shareholder);
      const newShareholder = await capTableService.createShareholder(shareholder);
      console.log('useCapTable: Created shareholder:', newShareholder);
      console.log('useCapTable: Current shareholders before update:', shareholders);
      setShareholders(prev => {
        const updated = [...prev, newShareholder];
        console.log('useCapTable: Updated shareholders list:', updated);
        return updated;
      });
      return newShareholder;
    } catch (err) {
      console.error('useCapTable: Error adding shareholder:', err);
      setError(err.message);
      throw err;
    }
  }, [shareholders]);

  const updateShareholder = useCallback(async (id, updates) => {
    try {
      const updatedShareholder = await capTableService.updateShareholder(id, updates);
      setShareholders(prev => prev.map(sh => sh.id === id ? updatedShareholder : sh));
      return updatedShareholder;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, []);

  const deleteShareholder = useCallback(async (id) => {
    try {
      await capTableService.deleteShareholder(id);
      setShareholders(prev => prev.filter(sh => sh.id !== id));
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, []);

  // CRUD operations for rounds
  const addRound = useCallback(async (roundData, participants) => {
    try {
      const newRound = await capTableService.createFinancingRound(roundData, participants);
      // The service now returns the round and all its new transactions
      setRounds(prev => [newRound.round, ...prev].sort((a, b) => new Date(b.date) - new Date(a.date)));
      setTransactions(prev => [...prev, ...newRound.transactions]);
      // Also update the shareholders list if the option pool was created/updated
      if (newRound.optionPoolTransaction) {
        // This is a simple way to refresh, a more targeted update could be done
        const shareholdersData = await capTableService.getShareholders();
        setShareholders(shareholdersData);
      }
      return newRound;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, []);

  const updateRound = useCallback(async (id, updates) => {
    try {
      const updatedRound = await capTableService.updateFinancingRound(id, updates);
      setRounds(prev => prev.map(r => r.id === id ? updatedRound : r));
      return updatedRound;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, []);

  const deleteRound = useCallback(async (id) => {
    try {
      await capTableService.deleteFinancingRound(id);
      setRounds(prev => prev.filter(r => r.id !== id));
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, []);

  // CRUD operations for transactions
  const addTransaction = useCallback(async (transaction) => {
    try {
      const newTransaction = await capTableService.createTransaction(transaction);
      setTransactions(prev => [...prev, newTransaction]);
      // Reload cap table to reflect changes
      await loadCapTableAtRound();
      return newTransaction;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, [loadCapTableAtRound]);

  const updateTransaction = useCallback(async (id, updates) => {
    try {
      const updatedTransaction = await capTableService.updateTransaction(id, updates);
      setTransactions(prev => prev.map(t => t.id === id ? updatedTransaction : t));
      // Reload cap table to reflect changes
      await loadCapTableAtRound();
      return updatedTransaction;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, [loadCapTableAtRound]);

  const deleteTransaction = useCallback(async (id) => {
    try {
      await capTableService.deleteTransaction(id);
      setTransactions(prev => prev.filter(t => t.id !== id));
      // Reload cap table to reflect changes
      await loadCapTableAtRound();
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, [loadCapTableAtRound]);

  // CRUD operations for preference terms
  const addPreferenceTerm = useCallback(async (term) => {
    try {
      const newTerm = await capTableService.createPreferenceTerm(term);
      setPreferenceTerms(prev => [...prev, newTerm]);
      return newTerm;
    } catch (err) {
      setError(err.message);
      throw err;
    }
  }, []);

  // Load data on mount
  useEffect(() => {
    loadData();
  }, [loadData]);

  return {
    // State
    loading,
    error,
    capTable,
    rounds,
    shareholders,
    shareClasses,
    transactions,
    preferenceTerms,

    // Actions
    loadData,
    loadCapTableAtRound,
    calculateExitScenario,

    // Shareholder operations
    addShareholder,
    updateShareholder,
    deleteShareholder,

    // Round operations
    addRound,
    updateRound,
    deleteRound,

    // Transaction operations
    addTransaction,
    updateTransaction,
    deleteTransaction,

    // Preference term operations
    addPreferenceTerm,

    // Utility
    clearError: () => setError(null)
  };
};
