import { PencilIcon } from '@heroicons/react/20/solid';

// --- MOCK DATA ---
const initialData = {
  shareholders: {
    'sh-1': { id: 'sh-1', name: 'Alice', role: 'Founder' },
    'sh-2': { id: 'sh-2', name: 'Bob', role: 'Founder' },
    'sh-3': { id: 'sh-3', name: 'Seed Fund A', role: 'Investor' },
    'sh-4': { id: 'sh-4', name: 'Carol', role: 'Advisor' },
    'sh-5': { id: 'sh-5', name: 'Series A Lead', role: 'Investor' },
  },
  shareClasses: {
    'sc-1': { id: 'sc-1', name: 'Common' },
    'sc-2': { id: 'sc-2', name: 'Seed Preferred' },
    'sc-3': { id: 'sc-3', name: 'Series A Preferred' },
  },
  rounds: [
    { name: 'Pre-Seed', date: '2022-01-15', preMoney: 500000, investment: 100000, shareClassId: 'sc-1' },
    { name: 'Seed', date: '2023-03-20', preMoney: 2000000, investment: 500000, shareClassId: 'sc-2' },
    { name: 'Series A', date: '2024-05-10', preMoney: 10000000, investment: 2500000, shareClassId: 'sc-3' },
  ],
  transactions: [
    // Pre-Seed
    { roundIndex: 0, shareholderId: 'sh-1', shares: 4000000, investment: 50000 },
    { roundIndex: 0, shareholderId: 'sh-2', shares: 4000000, investment: 50000 },
    // Seed
    { roundIndex: 1, shareholderId: 'sh-3', shares: 2000000, investment: 500000 },
    { roundIndex: 1, shareholderId: 'sh-4', shares: 50000, investment: 0 }, // Advisor grant
    // Series A
    { roundIndex: 2, shareholderId: 'sh-5', shares: 2500000, investment: 2500000 },
  ]
};

// --- UTILITY & FORMATTING ---
const formatCurrency = (value) => `Â£${(value / 1000000).toFixed(2)}M`;
const formatNumber = (value) => new Intl.NumberFormat('en-GB').format(value);
const formatPercent = (value) => `${(value * 100).toFixed(2)}%`;

// --- MAIN COMPONENT ---
const CapTableContent = () => {
  const [data, setData] = useState(initialData);
  const [isEditMode, setIsEditMode] = useState(false);
  const [viewMode, setViewMode] = useState('individual'); // individual | group
  const [selectedRoundIndex, setSelectedRoundIndex] = useState(data.rounds.length); // length = Current

  const derivedTable = useMemo(() => {
    let history = [];

    // Iterate through each round to build history
    for (let i = 0; i < data.rounds.length; i++) {
      const round = data.rounds[i];
      const prevHoldings = history[i - 1]?.holdings ?? {};
      let currentHoldings = { ...prevHoldings }; // Simple object spread instead of immer

      const roundTransactions = data.transactions.filter(t => t.roundIndex === i);

      for(const t of roundTransactions) {
        if (!currentHoldings[t.shareholderId]) {
          currentHoldings[t.shareholderId] = { shares: 0, investment: 0 };
        }
        currentHoldings[t.shareholderId].shares += t.shares;
        currentHoldings[t.shareholderId].investment += t.investment;
      }

      history.push({ name: round.name, date: round.date, preMoney: round.preMoney, investment: round.investment, holdings: currentHoldings, shareClassId: round.shareClassId });
    }

    // Now, calculate the state for the selected round
    const isCurrent = selectedRoundIndex === data.rounds.length;
    const relevantHistory = isCurrent ? history : history.slice(0, selectedRoundIndex + 1);
    const currentRoundState = relevantHistory[relevantHistory.length - 1];
    const previousRoundState = relevantHistory[relevantHistory.length - 2];

    if (!currentRoundState) return { displayRows: [], summary: {} }; // Handle Pre-seed case

    const postMoney = currentRoundState.preMoney + currentRoundState.investment;
    const totalShares = Object.values(currentRoundState.holdings).reduce((acc, h) => acc + h.shares, 0);

    const tableRows = Object.entries(currentRoundState.holdings).map(([shId, holding]) => {
      const shareholder = data.shareholders[shId];
      const ownership = holding.shares / totalShares;

      const prevOwnership = previousRoundState ? (previousRoundState.holdings[shId]?.shares ?? 0) / Object.values(previousRoundState.holdings).reduce((acc, h) => acc + h.shares, 0) : 0;
      const dilution = prevOwnership > 0 ? prevOwnership - ownership : 0;

      const lastTransaction = data.transactions.slice().reverse().find(t => t.shareholderId === shId && t.roundIndex === relevantHistory.length - 1);
      const sharePrice = lastTransaction?.investment > 0 ? lastTransaction.investment / lastTransaction.shares : currentRoundState.preMoney / Object.values(previousRoundState?.holdings ?? {}).reduce((acc,h) => acc + h.shares, 0) || 0;

      return {
        id: shId,
        name: shareholder.name,
        role: shareholder.role,
        shares: holding.shares,
        ownership,
        shareClass: data.shareClasses[currentRoundState.shareClassId]?.name,
        investment: holding.investment,
        sharePrice: sharePrice,
        dilution,
      };
    });

    const groupedRows = Object.values(tableRows.reduce((acc, row) => {
        const group = row.role === 'Founder' ? 'Founders' : row.role === 'Investor' ? 'Investors' : 'Employees & Advisors';
        if (!acc[group]) {
          acc[group] = { name: group, role: '', shares: 0, ownership: 0, investment: 0 };
        }
        acc[group].shares += row.shares;
        acc[group].ownership += row.ownership;
        acc[group].investment += row.investment;
        return acc;
    }, {}));


    const displayRows = viewMode === 'individual' ? tableRows : groupedRows;

    const summary = {
      totalShares: totalShares,
      totalRaised: history.reduce((acc, h) => acc + h.investment, 0),
      preMoney: currentRoundState.preMoney,
      postMoney: postMoney,
      founderOwnership: groupedRows.find(g => g.name === 'Founders')?.ownership ?? 0,
      investorOwnership: groupedRows.find(g => g.name === 'Investors')?.ownership ?? 0,
    };

    return { displayRows, summary };
  }, [data, selectedRoundIndex, viewMode]);

  const handleSharesChange = (shId, newShares) => {
    // This is a simplified update for demonstration in edit mode.
    // A real implementation would be much more complex.
    const newTx = { roundIndex: selectedRoundIndex, shareholderId: shId, shares: parseInt(newShares, 10) || 0, investment: 0};
    setData(prevData => ({
      ...prevData,
      transactions: [...prevData.transactions, newTx]
    }));
  }

  return (
    <div className="space-y-6">
      {/* Header Controls */}
      <div className="flex flex-wrap items-center justify-between gap-4">
        <div className="flex items-center space-x-4">
          <select 
            value={selectedRoundIndex}
            onChange={(e) => setSelectedRoundIndex(parseInt(e.target.value, 10))}
            className="rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 bg-purple-600 text-white font-medium px-4 py-2"
          >
            <option value={data.rounds.length}>Current</option>
            {data.rounds.map((r, i) => <option key={i} value={i}>{r.name}</option>)}
          </select>
          <div className="bg-gray-200 p-0.5 rounded-lg flex text-sm">
            <button
              onClick={() => setViewMode('individual')}
              className={`px-3 py-1 rounded-md ${viewMode === 'individual' ? 'bg-white shadow-sm' : ''}`}
            >
              Individuals
            </button>
            <button
              onClick={() => setViewMode('group')}
              className={`px-3 py-1 rounded-md ${viewMode === 'group' ? 'bg-white shadow-sm' : ''}`}
            >
              Groups
            </button>
          </div>
        </div>
        <button
          onClick={() => setIsEditMode(!isEditMode)}
          className={`flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-md border ${
            isEditMode
              ? 'bg-primary-600 text-white border-transparent'
              : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
          }`}
        >
          <PencilIcon className="h-4 w-4" />
          <span>{isEditMode ? 'Live Mode' : 'Edit Mode'}</span>
        </button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        <div className="bg-white p-4 rounded-lg shadow"><p className="text-sm text-gray-500">Pre-money Valuation</p><p className="text-xl font-bold">{formatCurrency(derivedTable.summary.preMoney)}</p></div>
        <div className="bg-white p-4 rounded-lg shadow"><p className="text-sm text-gray-500">Post-money Valuation</p><p className="text-xl font-bold">{formatCurrency(derivedTable.summary.postMoney)}</p></div>
        <div className="bg-white p-4 rounded-lg shadow"><p className="text-sm text-gray-500">Founders Ownership</p><p className="text-xl font-bold">{formatPercent(derivedTable.summary.founderOwnership)}</p></div>
        <div className="bg-white p-4 rounded-lg shadow"><p className="text-sm text-gray-500">Investors Ownership</p><p className="text-xl font-bold">{formatPercent(derivedTable.summary.investorOwnership)}</p></div>
      </div>

      {/* Cap Table */}
      <div className="overflow-x-auto">
        <div className="bg-white rounded-lg shadow min-w-full inline-block">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Shareholder</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Shares</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">% Ownership</th>
                {viewMode === 'individual' && <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Share Class</th>}
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Investment (Â£)</th>
                {viewMode === 'individual' && <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Share Price (Â£)</th>}
                {viewMode === 'individual' && <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dilution %</th>}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {derivedTable.displayRows.map(row => (
                <tr key={row.id || row.name}>
                  <td className="px-6 py-2 whitespace-nowrap"><div className="font-medium text-gray-900">{row.name}</div><div className="text-sm text-gray-500">{row.role}</div></td>
                  <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-700">
                    {isEditMode && viewMode === 'individual' ? (
                      <input type="text" defaultValue={row.shares} onBlur={(e) => handleSharesChange(row.id, e.target.value)} className="w-24 p-1 rounded border-gray-300"/>
                    ) : formatNumber(row.shares) }
                  </td>
                  <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-700">{formatPercent(row.ownership)}</td>
                  {viewMode === 'individual' && <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-700">{row.shareClass}</td>}
                  <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-700">{formatNumber(row.investment)}</td>
                  {viewMode === 'individual' && <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-700">{row.sharePrice?.toFixed(4)}</td>}
                  {viewMode === 'individual' && <td className="px-6 py-2 whitespace-nowrap text-sm text-red-500">{row.dilution > 0 ? formatPercent(row.dilution) : '-'}</td>}
                </tr>
              ))}
            </tbody>
            <tfoot className="bg-gray-50">
              <tr>
                <td className="px-6 py-2 text-left text-sm font-bold text-gray-700">Totals</td>
                <td className="px-6 py-2 text-left text-sm font-bold text-gray-700">{formatNumber(derivedTable.summary.totalShares)}</td>
                <td className="px-6 py-2 text-left text-sm font-bold text-gray-700">100.00%</td>
                 {viewMode === 'individual' && <td colSpan="1"></td>}
                <td className="px-6 py-2 text-left text-sm font-bold text-gray-700">{formatNumber(derivedTable.summary.totalRaised)}</td>
                {viewMode === 'individual' && <td colSpan="2"></td>}
              </tr>
            </tfoot>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CapTableContent;
