import React, { useState, useEffect, useRef } from 'react';
import { FolderIcon, DocumentTextIcon, MagnifyingGlassIcon, PlusIcon, ArrowUpTrayIcon, TrashIcon, EllipsisVerticalIcon, PencilSquareIcon, DocumentDuplicateIcon } from '@heroicons/react/24/outline';
import { getContracts, getContractFolders, deleteContract, createContractFolder, createContract, uploadContractPDF, debugRls, deleteContractFile } from '../../../services/legalService';
import { useAuth } from '../../../contexts/AuthContext';

const DropdownMenu = ({ contract, onEdit, onDelete, onDuplicate }) => {
  const [isOpen, setIsOpen] = useState(false);
  const menuRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [menuRef]);

  return (
    <div className="relative inline-block text-left" ref={menuRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="inline-flex items-center justify-center w-8 h-8 text-gray-500 rounded-full hover:bg-gray-100 hover:text-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
      >
        <EllipsisVerticalIcon className="w-5 h-5" />
      </button>

      {isOpen && (
        <div className="origin-top-right absolute right-0 mt-2 w-48 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 z-10">
          <div className="py-1" role="menu" aria-orientation="vertical" aria-labelledby="options-menu">
            <a href="#" onClick={(e) => { e.preventDefault(); onEdit(contract); setIsOpen(false); }} className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">
              <PencilSquareIcon className="w-4 h-4 mr-3" />
              Move to Folder
            </a>
            <a href="#" onClick={(e) => { e.preventDefault(); onDuplicate(contract); setIsOpen(false); }} className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">
              <DocumentDuplicateIcon className="w-4 h-4 mr-3" />
              Duplicate
            </a>
            <a href="#" onClick={(e) => { e.preventDefault(); onDelete(contract); setIsOpen(false); }} className="flex items-center px-4 py-2 text-sm text-red-600 hover:bg-gray-100" role="menuitem">
              <TrashIcon className="w-4 h-4 mr-3" />
              Delete
            </a>
          </div>
        </div>
      )}
    </div>
  );
};

const UploadModal = ({ showModal, onClose, folders, onUpload, saving }) => {
  const [uploadData, setUploadData] = useState({ name: '', description: '', folderId: '', file: null });

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/pdf') {
      setUploadData({ ...uploadData, file });
    } else {
      alert('Please select a PDF file');
      e.target.value = '';
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpload(uploadData);
  };

  if (!showModal) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Upload Contract</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Contract Name *</label>
            <input
              type="text"
              value={uploadData.name}
              onChange={(e) => setUploadData({ ...uploadData, name: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
            <textarea
              value={uploadData.description}
              onChange={(e) => setUploadData({ ...uploadData, description: e.target.value })}
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Folder (Optional)</label>
            <select
              value={uploadData.folderId}
              onChange={(e) => setUploadData({ ...uploadData, folderId: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select a folder</option>
              {folders.map(folder => (
                <option key={folder.id} value={folder.id}>{folder.name}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">PDF File *</label>
            <input
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
              disabled={saving}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={saving}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
            >
              {saving ? 'Uploading...' : 'Upload Contract'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const FolderModal = ({ showModal, onClose, onCreate, saving }) => {
  const [newFolder, setNewFolder] = useState({ name: '', description: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    onCreate(newFolder);
  };

  if (!showModal) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Create New Folder</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Folder Name *</label>
            <input
              type="text"
              value={newFolder.name}
              onChange={(e) => setNewFolder({ ...newFolder, name: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
            <textarea
              value={newFolder.description}
              onChange={(e) => setNewFolder({ ...newFolder, description: e.target.value })}
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
              disabled={saving}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={saving}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
            >
              {saving ? 'Creating...' : 'Create Folder'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const Contracts = () => {
  const [contracts, setContracts] = useState([]);
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showFolderModal, setShowFolderModal] = useState(false);
  const [saving, setSaving] = useState(false);
  const { currentOrganization } = useAuth();

  useEffect(() => {
    loadContracts();
    loadFolders();
  }, [currentOrganization]);

  const loadContracts = async () => {
    setLoading(true);
    try {
      if (!currentOrganization) return;
      const data = await getContracts(currentOrganization.organization_id);
      setContracts(data);
    } catch (error) {
      console.error('Error loading contracts:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadFolders = async () => {
    try {
      if (!currentOrganization) return;
      const data = await getContractFolders(currentOrganization.organization_id);
      setFolders(data);
    } catch (error) {
      console.error('Error loading folders:', error);
    }
  };

  const handleDeleteContract = async (contract) => {
    if (!contract || !contract.id) return;
    if (window.confirm(`Are you sure you want to delete the contract "${contract.name}"?`)) {
      try {
        // First, attempt to delete the associated file from storage if it exists
        if (contract.pdf_file_path) {
          await deleteContractFile(contract.pdf_file_path);
        }

        // Then, delete the contract record from the database
        await deleteContract(contract.id);

        await loadContracts(); // Reload the list
        alert('Contract deleted successfully.');
      } catch (error) {
        console.error('Error deleting contract:', error);
        alert('Error deleting contract. Please check the console for details.');
      }
    }
  };

  const handleUploadContract = async (uploadData) => {
    if (!uploadData.name || !uploadData.file) {
      alert('Please fill in the contract name and select a file');
      return;
    }

    setSaving(true);
    try {
      if (!currentOrganization || !currentOrganization.organization_id) {
        alert("CRITICAL: No organization is currently selected, or the organization is invalid. Please use the switcher at the top right to select an organization.");
        throw new Error("No organization selected.");
      }

      const debugInfo = await debugRls(currentOrganization.organization_id);
      if (debugInfo) {
        alert(`
          --- RLS DEBUG INFO ---
          Org ID Sent: ${currentOrganization.organization_id}
          User ID Seen by DB: ${debugInfo.current_user_id}
          DB Says Is Member?: ${debugInfo.is_member}
        `);

        if (!debugInfo.is_member) {
          alert("STOP: The database confirms you are NOT a member of this organization. The request will be blocked. Please check the user_organizations table.");
          setSaving(false);
          return;
        }
      }

      // First create the contract record
      const contractRecord = {
        name: uploadData.name,
        description: uploadData.description,
        folder_id: uploadData.folderId || null,
      };

      const newContract = await createContract(contractRecord, currentOrganization.organization_id);

      if (!newContract || !newContract.id) {
        console.error("[Contracts.js] Failed to create contract record or get an ID back from the database.", newContract);
        alert("Error: Did not receive a valid record from the database after creation. Aborting file upload.");
        setSaving(false);
        return;
      }

      // Then upload the PDF file
      await uploadContractPDF(uploadData.file, newContract.id, currentOrganization.organization_id);

      alert('Contract uploaded successfully!');
      setShowUploadModal(false);
      await loadContracts();
    } catch (error) {
      console.error('Error uploading contract:', error);
      alert(`Error uploading contract: ${error.message}`);
    } finally {
      setSaving(false);
    }
  };

  const handleCreateFolder = async (newFolder) => {
    if (!newFolder.name.trim()) {
      alert('Please enter a folder name');
      return;
    }

    setSaving(true);
    try {
      if (!currentOrganization) {
        throw new Error("No organization selected.");
      }
      await createContractFolder(
        {
          name: newFolder.name,
          description: newFolder.description,
        },
        currentOrganization.organization_id
      );

      setShowFolderModal(false);
      await loadFolders();
    } catch (error) {
      console.error('Error creating folder:', error);
      alert(`Error creating folder: ${error.message}`);
    } finally {
      setSaving(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'draft': return 'bg-yellow-100 text-yellow-800';
      case 'expired': return 'bg-red-100 text-red-800';
      case 'archived': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="space-y-8">
        <div>
          <h3 className="text-xl font-bold text-gray-900">My Contracts</h3>
          <p className="text-sm text-gray-500 mt-1">Manage your legal contracts and agreements.</p>
        </div>
        <div className="text-center py-20">
          <p className="text-gray-500">Loading contracts...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h3 className="text-xl font-bold text-gray-900">My Contracts</h3>
        <p className="text-sm text-gray-500 mt-1">Manage your legal contracts and agreements.</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
        {/* Left Column - Folders */}
        <div className="md:col-span-1">
          <div className="bg-white p-4 rounded-lg shadow border border-gray-200">
            <h4 className="text-sm font-semibold text-gray-700 mb-4">Folders</h4>
            <div className="space-y-2">
              <button
                onClick={() => setSelectedFolder('all')}
                className={`w-full flex items-center p-2 text-left text-sm rounded-md transition-colors ${
                  selectedFolder === 'all'
                    ? 'bg-blue-100 text-blue-700'
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                <FolderIcon className="h-4 w-4 mr-2" />
                All Contracts
              </button>
              {folders.map(folder => (
                <button
                  key={folder.id}
                  onClick={() => setSelectedFolder(folder.id)}
                  className={`w-full flex items-center p-2 text-left text-sm rounded-md transition-colors ${
                    selectedFolder === folder.id
                      ? 'bg-blue-100 text-blue-700'
                      : 'text-gray-700 hover:bg-gray-100'
                  }`}
                >
                  <FolderIcon className="h-4 w-4 mr-2" />
                  {folder.name}
                </button>
              ))}
              <button
                onClick={() => setShowFolderModal(true)}
                className="w-full flex items-center p-2 text-left text-sm rounded-md transition-colors text-blue-600 hover:bg-blue-50 border border-dashed border-blue-300 mt-4"
              >
                <PlusIcon className="h-4 w-4 mr-2" />
                Add Folder
              </button>
            </div>
          </div>
        </div>

        {/* Right Column - Contracts */}
        <div className="md:col-span-3">
          <div className="bg-white rounded-lg shadow border border-gray-200">
            <div className="p-6 border-b border-gray-200">
              <div className="flex justify-between items-center">
                <div className="flex-1 relative max-w-md">
                  <MagnifyingGlassIcon className="pointer-events-none absolute top-2.5 left-3 h-5 w-5 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Search contracts..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md"
                  />
                </div>
                <div className="flex items-center space-x-3">
                  <span className="text-sm text-gray-500">
                    {contracts.length} contract{contracts.length !== 1 ? 's' : ''}
                  </span>
                  <button
                    onClick={() => setShowUploadModal(true)}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
                  >
                    <ArrowUpTrayIcon className="h-5 w-5 mr-2" />
                    Upload Contract
                  </button>
                </div>
              </div>
            </div>

            <div className="p-6">
              {contracts.length === 0 ? (
                <div className="text-center py-12">
                  <DocumentTextIcon className="mx-auto h-12 w-12 text-gray-400" />
                  <h3 className="mt-2 text-sm font-medium text-gray-900">No contracts found</h3>
                  <p className="mt-1 text-sm text-gray-500">
                    {searchTerm || selectedFolder !== 'all' 
                      ? 'Try adjusting your search or folder filter.'
                      : 'Get started by uploading a contract or creating one from a template.'
                    }
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Contract Name
                        </th>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Created At
                        </th>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Status
                        </th>
                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Actions
                        </th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {contracts
                        .filter(contract => selectedFolder === 'all' || contract.folder_id === selectedFolder)
                        .filter(contract => searchTerm === '' || contract.name.toLowerCase().includes(searchTerm.toLowerCase()))
                        .map(contract => (
                        <tr key={contract.id}>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{contract.name}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{new Date(contract.created_at).toLocaleDateString()}</td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusColor(contract.status)}`}>
                              {contract.status}
                            </span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                            <DropdownMenu
                              contract={contract}
                              onDelete={handleDeleteContract}
                              onEdit={() => {}} // Placeholder
                              onDuplicate={() => {}} // Placeholder
                            />
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <UploadModal 
        showModal={showUploadModal}
        onClose={() => setShowUploadModal(false)}
        folders={folders}
        onUpload={handleUploadContract}
        saving={saving}
      />
      <FolderModal 
        showModal={showFolderModal}
        onClose={() => setShowFolderModal(false)}
        onCreate={handleCreateFolder}
        saving={saving}
      />
    </div>
  );
};

export default Contracts;
