import { supabase } from '../lib/supabase';

// Shareholders
export const capTableService = {
  // Shareholders
  async getShareholders() {
    const { data, error } = await supabase
      .from('shareholders')
      .select('*')
      .order('name');

    if (error) throw error;
    return data;
  },

  async createShareholder(shareholder) {
    const { data, error } = await supabase
      .from('shareholders')
      .insert([shareholder])
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  async updateShareholder(id, updates) {
    const { data, error } = await supabase
      .from('shareholders')
      .update(updates)
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  async deleteShareholder(id) {
    const { error } = await supabase
      .from('shareholders')
      .delete()
      .eq('id', id);

    if (error) throw error;
  },

  // Share Classes
  async getShareClasses() {
    const { data, error } = await supabase
      .from('share_classes')
      .select('*')
      .order('name');

    if (error) throw error;
    return data;
  },

  async createShareClass(shareClass) {
    const { data, error } = await supabase
      .from('share_classes')
      .insert([shareClass])
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  // Financing Rounds
  async getFinancingRounds() {
    const { data, error } = await supabase
      .from('financing_rounds')
      .select(`
        *,
        share_classes(name)
      `)
      .order('date', { ascending: false });

    if (error) throw error;
    return data;
  },

  async createFinancingRound(round, participants) {
    // This function needs to perform several steps in a single transaction
    // to ensure data integrity. We will use a Supabase Edge Function (or RPC call)
    // to handle this atomically on the server side.

    // Explicitly stringify the participants array to avoid any ambiguity
    // in how the Supabase client library handles the data type.
    const participantsAsJsonString = JSON.stringify(participants.map(p => ({
      shareholder_id: p.shareholder_id,
      investment_amount: p.investment_amount,
      shares_issued: p.shares_issued,
      preference_multiplier: p.preference_multiplier || 0,
    })));

    const { data, error } = await supabase.rpc('create_financing_round_with_options', {
      round_name: round.name,
      round_date: round.date,
      pre_money_valuation: round.pre_money_valuation,
      share_class_id: round.share_class_id,
      option_pool_shares: round.option_pool_shares, // Pass number of shares directly
      participants: participantsAsJsonString // Send the pre-stringified JSON
    });

    if (error) {
      console.error('Error creating financing round with options:', error);
      throw error;
    }

    // The RPC function should return the newly created round and transactions
    return data;
  },

  async updateFinancingRound(id, updates) {
    const { data, error } = await supabase
      .from('financing_rounds')
      .update(updates)
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  async deleteFinancingRound(id) {
    const { error } = await supabase
      .from('financing_rounds')
      .delete()
      .eq('id', id);

    if (error) throw error;
  },

  // Transactions
  async getTransactions(roundId = null) {
    try {
      let query = supabase
        .from('transactions')
        .select(`
          *,
          shareholders(name, role),
          financing_rounds(name, date)
        `)
        .order('created_at');

      if (roundId) {
        query = query.eq('round_id', roundId);
      }

      const { data, error } = await query;
      if (error) throw error;

      // Fetch share classes separately if needed
      if (data && data.length > 0) {
        const roundIds = [...new Set(data.map(tx => tx.round_id).filter(Boolean))];
        if (roundIds.length > 0) {
          const { data: roundsData } = await supabase
            .from('financing_rounds')
            .select('id, share_classes(name)')
            .in('id', roundIds);

          const roundMap = {};
          roundsData?.forEach(round => {
            roundMap[round.id] = round.share_classes?.name || 'Common';
          });

          // Add share class to transactions
          data.forEach(tx => {
            tx.shareClass = roundMap[tx.round_id] || 'Common';
          });
        }
      }

      return data || [];
    } catch (error) {
      console.error('Error in getTransactions:', error);
      throw error;
    }
  },

  async createTransaction(transaction) {
    const { data, error } = await supabase
      .from('transactions')
      .insert([transaction])
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  async updateTransaction(id, updates) {
    const { data, error } = await supabase
      .from('transactions')
      .update(updates)
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  async deleteTransaction(id) {
    const { error } = await supabase
      .from('transactions')
      .delete()
      .eq('id', id);

    if (error) throw error;
  },

  // Preference Terms
  async getPreferenceTerms() {
    const { data, error } = await supabase
      .from('preference_terms')
      .select(`
        *,
        share_classes(name)
      `);

    if (error) throw error;
    return data;
  },

  async createPreferenceTerm(term) {
    const { data, error } = await supabase
      .from('preference_terms')
      .insert([term])
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  // Cap Table Calculations
  async getCapTableAtRound(roundId = null) {
    try {
      // If no round is specified, fetch all transactions for the "Current" state.
      if (!roundId) {
        const { data: allTransactions, error: allTxError } = await supabase
          .from('transactions')
          .select(`*, shareholders(name, role)`);

        if (allTxError) throw allTxError;

        await this.attachShareClassesToTransactions(allTransactions);
        return this.calculateCapTableFromTransactions(allTransactions || []);
      }

      // If a specific round IS specified:
      // 1. Fetch all rounds to establish the full chronology.
      const { data: allRounds, error: roundsError } = await supabase
        .from('financing_rounds')
        .select('id, date')
        .order('date', { ascending: true }); // Order from oldest to newest.

      if (roundsError) throw roundsError;

      // 2. Find the date of the selected round.
      const selectedRound = allRounds.find(r => r.id === roundId);
      if (!selectedRound) {
        // If round not found, return an empty state.
        return this.calculateCapTableFromTransactions([]);
      }

      // 3. Identify all rounds that occurred up to and including the selected one.
      const relevantRoundIds = allRounds
        .filter(r => new Date(r.date) <= new Date(selectedRound.date))
        .map(r => r.id);

      // 4. Fetch all transactions belonging to those cumulative rounds.
      const { data: transactions, error: txError } = await supabase
        .from('transactions')
        .select(`*, shareholders(name, role)`)
        .in('round_id', relevantRoundIds);

      if (txError) throw txError;

      // 5. Attach share class info and calculate the final cap table.
      await this.attachShareClassesToTransactions(transactions);
      return this.calculateCapTableFromTransactions(transactions || []);

    } catch (error) {
      console.error('Error in getCapTableAtRound:', error);
      throw error;
    }
  },

  async attachShareClassesToTransactions(transactions) {
    if (!transactions || transactions.length === 0) {
      return;
    }
    const roundIds = [...new Set(transactions.map(tx => tx.round_id).filter(Boolean))];
    if (roundIds.length === 0) {
      return;
    }

    const { data: roundsData } = await supabase
      .from('financing_rounds')
      .select('id, share_classes(name)')
      .in('id', roundIds);

    const roundMap = {};
    roundsData?.forEach(round => {
      roundMap[round.id] = round.share_classes?.name || 'Common';
    });

    transactions.forEach(tx => {
      tx.shareClass = roundMap[tx.round_id] || 'Common';
    });
  },

  calculateCapTableFromTransactions(transactions) {
    const holdings = {};
    let totalShares = 0;

    // Aggregate shares by shareholder
    transactions.forEach(tx => {
      const shareholderId = tx.shareholder_id;
      if (!holdings[shareholderId]) {
        holdings[shareholderId] = {
          id: shareholderId,
          name: tx.shareholders?.name || 'Unknown',
          role: tx.shareholders?.role || 'Unknown',
          shares: 0,
          investment: 0,
          shareClass: tx.shareClass || 'Common',
        };
      }

      holdings[shareholderId].shares += parseFloat(tx.shares_issued || 0);
      holdings[shareholderId].investment += parseFloat(tx.investment_amount || 0);
      totalShares += parseFloat(tx.shares_issued || 0);
    });

    // Calculate ownership percentages
    const capTable = Object.values(holdings).map(holding => ({
      ...holding,
      ownership: totalShares > 0 ? (holding.shares / totalShares) * 100 : 0,
    }));

    return {
      capTable,
      totalShares,
      summary: {
        totalShares,
        totalInvestment: capTable.reduce((sum, h) => sum + h.investment, 0),
      }
    };
  },

  // Exit Scenario Calculations
  async calculateExitScenario(acquisitionAmount, acquisitionPercentage, preferenceType = 'non-participating') {
    const { capTable } = await this.getCapTableAtRound();
    const actualAcquisitionValue = (acquisitionAmount * acquisitionPercentage) / 100;

    // Get preference terms
    const preferenceTerms = await this.getPreferenceTerms();

    return this.calculateExitValues(capTable, actualAcquisitionValue, preferenceTerms, preferenceType);
  },

  calculateExitValues(capTable, acquisitionValue, preferenceTerms, preferenceType) {
    // Calculate preference payouts
    const preferencePayouts = capTable
      .filter(shareholder => shareholder.shareClass.includes('Preferred'))
      .map(shareholder => {
        const term = preferenceTerms.find(t => t.share_classes.name === shareholder.shareClass);
        const multiplier = term ? term.multiplier : 1;
        const preferenceAmount = shareholder.investment * multiplier;
        const conversionValue = (shareholder.ownership / 100) * acquisitionValue;

        let finalValue;
        if (preferenceType === 'non-participating') {
          finalValue = Math.max(preferenceAmount, conversionValue);
        } else {
          finalValue = preferenceAmount + conversionValue;
        }

        return {
          ...shareholder,
          preferenceAmount,
          conversionValue,
          finalValue,
          tookPreference: preferenceType === 'participating' || preferenceAmount > conversionValue,
          multiplier,
        };
      });

    // Calculate remaining proceeds
    const totalPreferencePayout = preferencePayouts.reduce((sum, p) => sum + p.finalValue, 0);
    const remainingProceeds = Math.max(0, acquisitionValue - totalPreferencePayout);

    // Distribute remaining to common shareholders
    const commonShareholders = capTable.filter(s => s.shareClass === 'Common');
    const totalCommonOwnership = commonShareholders.reduce((sum, s) => sum + s.ownership, 0);

    const exitCalculations = capTable.map(shareholder => {
      if (shareholder.shareClass.includes('Preferred')) {
        const preferencePayout = preferencePayouts.find(p => p.id === shareholder.id);
        return preferencePayout;
      } else {
        const commonShare = totalCommonOwnership > 0 ? (shareholder.ownership / totalCommonOwnership) * remainingProceeds : 0;
        return {
          ...shareholder,
          preferenceAmount: 0,
          conversionValue: commonShare,
          finalValue: commonShare,
          tookPreference: false,
          multiplier: 1,
        };
      }
    });

    return {
      exitCalculations,
      totalExitValue: exitCalculations.reduce((sum, calc) => sum + calc.finalValue, 0),
      totalPreferencePayout,
      remainingProceeds,
    };
  }
};
