import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const SecondaryNav = ({ activeTab, setActiveTab }) => {
  const navigate = useNavigate();
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      setIsScrolled(scrollTop > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const tabs = [
    { name: 'Company Setup', path: '/company-setup' },
    { name: 'Finance', path: '/finance' },
    { name: 'Cap Table', path: '/cap-table' },
    { name: 'Marketing', path: '/marketing' },
    { name: 'HR', path: '/hr' }
  ];

  const handleTabClick = (tab) => {
    setActiveTab(tab.name);
    navigate(tab.path);
  };

  return (
    <nav className={`fixed top-0 left-0 right-0 z-40 transition-all duration-300 ${
      isScrolled ? 'bg-white border-b border-gray-200' : 'bg-purple-900'
    }`} style={{ top: isScrolled ? '0' : '80px' }}>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.name}
              onClick={() => handleTabClick(tab)}
              className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors duration-200 ${
                activeTab === tab.name
                  ? isScrolled 
                    ? 'border-primary-500 text-primary-600'
                    : 'border-white text-white'
                  : isScrolled
                    ? 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    : 'border-transparent text-purple-200 hover:text-white hover:border-purple-300'
              }`}
            >
              {tab.name}
            </button>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default SecondaryNav;
