import React, { useState } from 'react';
import { useCapTable } from '../../hooks/useCapTable';
import CapTableContent from './components/captable/CapTableContent';
import RoundHistory from './components/roundhistory/RoundHistory';
import ExitScenarios from './components/exitscenarios/ExitScenarios';
import InvestmentPlanning from './components/investment/InvestmentPlanning';
import AddShareClassForm from './components/captable/AddShareClassForm';
import Modal from '../../components/common/layout/Modal';

const CapTable = ({ activeSubTab, onSubTabChange }) => {
  const {
    addShareClass,
  } = useCapTable();

  const [isAddShareClassModalOpen, setAddShareClassModalOpen] = useState(false);
  const [isAddRoundModalOpen, setAddRoundModalOpen] = useState(false);

  const handleAddShareClass = async (shareClassData) => {
    await addShareClass(shareClassData);
    setAddShareClassModalOpen(false);
  };

  const handleAddRound = () => {
    setAddRoundModalOpen(true);
  };

  const renderContent = () => {
    if (loading) {
      return (
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      );
    }

    if (error) {
      return (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Error loading cap table data</h3>
              <div className="mt-2 text-sm text-red-700">
                <p>{error}</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    switch (activeSubTab) {
      case 'overview':
        return <CapTableContent />;
      case 'rounds':
        return <RoundHistory onAddRound={handleAddRound} />;
      case 'shareholders':
        return (
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-xl font-bold text-gray-900">Shareholders</h2>
              <button
                type="button"
                onClick={() => setAddShareClassModalOpen(true)}
                className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                Manage Share Classes
              </button>
            </div>
            {/* Shareholder table content would go here */}
          </div>
        );
      case 'exits':
        return <ExitScenarios />;
      case 'investment':
        return <InvestmentPlanning />;
      default:
        return <CapTableContent />;
    }
  };

  return (
    <div className="space-y-6">
      {renderContent()}

      <Modal isOpen={isAddShareholderModalOpen} onClose={() => setAddShareholderModalOpen(false)}>
        {/* Placeholder for AddShareholderForm */}
        <div className="p-4">
          <h3 className="text-lg font-medium">Add New Shareholder</h3>
          <p>Form to add a new shareholder will be here.</p>
        </div>
      </Modal>

      <Modal isOpen={isAddShareClassModalOpen} onClose={() => setAddShareClassModalOpen(false)}>
        <AddShareClassForm
          onAdd={handleAddShareClass}
          onCancel={() => setAddShareClassModalOpen(false)}
        />
      </Modal>

      <Modal isOpen={isAddRoundModalOpen} onClose={() => setAddRoundModalOpen(false)}>
        <div className="p-4">
          <h3 className="text-lg font-medium">Add New Round</h3>
          <p>Form to add a new round will be here.</p>
        </div>
      </Modal>
    </div>
  );
};

export default CapTable;
