import React, { useState } from 'react';
import SubNav from '../../components/common/layout/SubNav';
import CompanySetupDashboard from './components/CompanySetupDashboard';
import CompanyDetails from './components/details/CompanyDetails';
import FoundationDocuments from './components/foundation/FoundationDocuments';
import FinancialSetup from './components/financial/FinancialSetup';
import LegalCompliance from './components/legal/LegalCompliance';

const CompanySetup = () => {
  const [activeSubTab, setActiveSubTab] = useState('dashboard');

  const subTabs = [
    { id: 'dashboard', name: 'Dashboard' },
    { id: 'details', name: 'Company Details' },
    { id: 'foundation', name: 'Foundation Documents' },
    { id: 'financial', name: 'Financial Setup' },
    { id: 'legal', name: 'Legal & Compliance' },
  ];

  const renderContent = () => {
    switch (activeSubTab) {
      case 'dashboard':
        return <CompanySetupDashboard />;
      case 'details':
        return <CompanyDetails />;
      case 'foundation':
        return <FoundationDocuments />;
      case 'financial':
        return <FinancialSetup />;
      case 'legal':
        return <LegalCompliance />;
      default:
        return <CompanySetupDashboard />;
    }
  };

  return (
    <div>
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Company Setup</h1>
      </div>
      <SubNav
        subTabs={subTabs}
        activeSubTab={activeSubTab}
        onSubTabChange={setActiveSubTab}
      />
      <div className="mt-8">
        {renderContent()}
      </div>
    </div>
  );
};

export default CompanySetup;
