import React, { useState, useEffect } from 'react';
import { DocumentTextIcon, MagnifyingGlassIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { getTemplates, createContractFromTemplate, getContractFolders } from '../../../services/legalService';

const Templates = () => {
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showCustomizeModal, setShowCustomizeModal] = useState(false);
  const [customizedData, setCustomizedData] = useState({});
  const [customizedName, setCustomizedName] = useState('');
  const [folders, setFolders] = useState([]);
  const [selectedFolder, setSelectedFolder] = useState('');
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    loadTemplates();
    loadFolders();
  }, []);

  const loadTemplates = async () => {
    try {
      const data = await getTemplates();
      setTemplates(data);
      if (data.length > 0) {
        setSelectedTemplate(data[0]);
      }
    } catch (error) {
      console.error('Error loading templates:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadFolders = async () => {
    try {
      const data = await getContractFolders();
      setFolders(data);
    } catch (error) {
      console.error('Error loading folders:', error);
    }
  };

  const filteredTemplates = templates.filter(template =>
    template.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    template.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCustomize = () => {
    setCustomizedName(`${selectedTemplate.name} - Customized`);
    setCustomizedData({});
    setSelectedFolder('');
    setShowCustomizeModal(true);
  };

  const handleFieldChange = (key, value) => {
    setCustomizedData(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleSaveCustomized = async () => {
    if (!customizedName.trim()) {
      alert('Please enter a contract name');
      return;
    }

    setSaving(true);
    try {
      const contractData = {
        name: customizedName,
        description: `Customized version of ${selectedTemplate.name}`,
        data: customizedData,
        folderId: selectedFolder || null,
        effectiveDate: customizedData.effectiveDate || customizedData.startDate || null,
        expiryDate: customizedData.expiryDate || null
      };

      const contractId = await createContractFromTemplate(selectedTemplate.id, contractData);

      alert('Customized contract saved to My Contracts!');
      setShowCustomizeModal(false);
      setCustomizedData({});
      setCustomizedName('');
      setSelectedFolder('');
    } catch (error) {
      console.error('Error saving contract:', error);
      alert('Error saving contract. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  const CustomizeModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-xl font-bold text-gray-900">Customize {selectedTemplate?.name}</h3>
          <button
            onClick={() => setShowCustomizeModal(false)}
            className="text-gray-400 hover:text-gray-600"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        <div className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Contract Name</label>
            <input
              type="text"
              value={customizedName}
              onChange={(e) => setCustomizedName(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a name for this contract"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Folder</label>
            <select
              value={selectedFolder}
              onChange={(e) => setSelectedFolder(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">Select a folder (optional)</option>
              {folders.map(folder => (
                <option key={folder.id} value={folder.id}>{folder.name}</option>
              ))}
            </select>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {selectedTemplate?.fields?.map(field => (
              <div key={field.key}>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  {field.label}
                </label>
                {field.type === 'textarea' ? (
                  <textarea
                    value={customizedData[field.key] || ''}
                    onChange={(e) => handleFieldChange(field.key, e.target.value)}
                    placeholder={field.placeholder}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                ) : (
                  <input
                    type={field.type}
                    value={customizedData[field.key] || ''}
                    onChange={(e) => handleFieldChange(field.key, e.target.value)}
                    placeholder={field.placeholder}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                )}
              </div>
            ))}
          </div>

          <div className="bg-gray-50 p-4 rounded-md">
            <h4 className="text-sm font-medium text-gray-700 mb-2">Preview</h4>
            <div className="text-sm text-gray-600 space-y-2">
              <p><strong>Template:</strong> {selectedTemplate?.name}</p>
              <p><strong>Custom Name:</strong> {customizedName}</p>
              <p><strong>Folder:</strong> {folders.find(f => f.id === selectedFolder)?.name || 'None'}</p>
              {Object.entries(customizedData).map(([key, value]) => (
                <p key={key}>
                  <strong>{key}:</strong> {value}
                </p>
              ))}
            </div>
          </div>

          <div className="flex justify-end space-x-3">
            <button
              onClick={() => setShowCustomizeModal(false)}
              className="px-4 py-2 text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
              disabled={saving}
            >
              Cancel
            </button>
            <button
              onClick={handleSaveCustomized}
              disabled={saving}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
            >
              {saving ? 'Saving...' : 'Save to My Contracts'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="space-y-8">
        <div>
          <h3 className="text-xl font-bold text-gray-900">Templates Library</h3>
          <p className="text-sm text-gray-500 mt-1">Browse and customize pre-built legal templates for your business.</p>
        </div>
        <div className="text-center py-20">
          <p className="text-gray-500">Loading templates...</p>
        </div>
      </div>
    );
  }

  // Group templates by category
  const templatesByCategory = filteredTemplates.reduce((acc, template) => {
    if (!acc[template.category]) {
      acc[template.category] = [];
    }
    acc[template.category].push(template);
    return acc;
  }, {});

  return (
    <div className="space-y-8">
      <div>
        <h3 className="text-xl font-bold text-gray-900">Templates Library</h3>
        <p className="text-sm text-gray-500 mt-1">Browse and customize pre-built legal templates for your business.</p>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Left Column */}
        <div className="md:col-span-1 space-y-6">
          <div className="relative">
            <MagnifyingGlassIcon className="pointer-events-none absolute top-3.5 left-4 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search templates..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-11 pr-4 py-2 border border-gray-300 rounded-md"
            />
          </div>
          <div className="space-y-4">
            {Object.entries(templatesByCategory).map(([category, categoryTemplates]) => (
              <div key={category}>
                <h3 className="text-xs font-semibold uppercase text-gray-500 mb-2">{category}</h3>
                <ul className="space-y-1">
                  {categoryTemplates.map(template => (
                    <li key={template.id}>
                      <button
                        onClick={() => setSelectedTemplate(template)}
                        className={`w-full flex items-center p-3 text-left text-sm rounded-md transition-colors ${
                          selectedTemplate?.id === template.id
                            ? 'bg-blue-100 text-blue-700'
                            : 'text-gray-700 hover:bg-gray-100'
                        }`}
                      >
                        <DocumentTextIcon className="h-5 w-5 mr-3" />
                        {template.name}
                      </button>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>

        {/* Right Column */}
        <div className="md:col-span-2">
          <div className="bg-white p-6 rounded-lg shadow border border-gray-200 sticky top-8">
            {selectedTemplate ? (
              <div className="space-y-6">
                <div className="flex justify-between items-center">
                  <h2 className="text-xl font-bold text-gray-900">{selectedTemplate.name}</h2>
                  <button 
                    onClick={handleCustomize}
                    className="px-4 py-2 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700"
                  >
                    Customise Template
                  </button>
                </div>
                <div className="prose prose-sm max-w-none p-4 border rounded-md bg-gray-50 h-96 overflow-y-auto">
                  <p>{selectedTemplate.template_content}</p>
                  <p className="text-sm text-gray-500 mt-4">
                    <strong>Category:</strong> {selectedTemplate.category}
                  </p>
                  {selectedTemplate.description && (
                    <p className="text-sm text-gray-500">
                      <strong>Description:</strong> {selectedTemplate.description}
                    </p>
                  )}
                </div>
              </div>
            ) : (
              <div className="text-center py-20">
                <p className="text-gray-500">Select a template to preview</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {showCustomizeModal && <CustomizeModal />}
    </div>
  );
};

export default Templates;
