import React, { useState } from 'react';
import MainLayout from './components/common/layout/MainLayout';
import Dashboard from './pages/Dashboard/Dashboard.old';
import CompanySetup from './pages/company-setup';
import Finance from './pages/finance';
import CapTable from './pages/cap-table';
import Marketing from './pages/marketing';
import HR from './pages/hr';
import Marketplace from './pages/marketplace';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');

  const tabs = [
    { id: 'dashboard', name: 'Dashboard' },
    { id: 'company', name: 'Company Setup' },
    { id: 'finance', name: 'Finance' },
    { id: 'captable', name: 'Cap Table' },
    { id: 'marketing', name: 'Marketing' },
    { id: 'hr', name: 'HR' },
    // Marketplace is not a main tab, it will be handled separately
  ];

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard />;
      case 'company':
        return <CompanySetup />;
      case 'finance':
        return <Finance />;
      case 'captable':
        return <CapTable />;
      case 'marketing':
        return <Marketing />;
      case 'hr':
        return <HR />;
      case 'marketplace':
        return <Marketplace />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <MainLayout
      tabs={tabs}
      activeTab={activeTab}
      onTabChange={setActiveTab}
      showSecondaryNav={activeTab !== 'marketplace'} // Hide secondary nav on marketplace page
    >
      {renderContent()}
    </MainLayout>
  );
}

export default App;
