import React, { useState } from 'react';

const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

const initialRows = [
  { id: 1, type: 'revenue', label: 'Product Sales', values: [12000, 13000, 14000, 15000, 16000, 17000, 18000, 19000, 20000, 21000, 22000, 23000] },
  { id: 2, type: 'revenue', label: 'Consulting', values: [4000, 4200, 4300, 4400, 4500, 4600, 4700, 4800, 4900, 5000, 5100, 5200] },
  { id: 3, type: 'expense', label: 'Salaries', values: [-8000, -8200, -8300, -8400, -8500, -8600, -8700, -8800, -8900, -9000, -9100, -9200] },
  { id: 4, type: 'expense', label: 'Marketing', values: [-2000, -2100, -2200, -2300, -2400, -2500, -2600, -2700, -2800, -2900, -3000, -3100] },
];

const Budget = () => {
  const [rows, setRows] = useState(initialRows);
  const [newLabel, setNewLabel] = useState('');
  const [newType, setNewType] = useState('revenue');
  const [expandRevenue, setExpandRevenue] = useState(true);
  const [expandCosts, setExpandCosts] = useState(true);

  const handleValueChange = (rowIdx, monthIdx, value) => {
    const updated = rows.map((row, i) =>
      i === rowIdx
        ? { ...row, values: row.values.map((v, j) => (j === monthIdx ? Number(value) : v)) }
        : row
    );
    setRows(updated);
  };

  const handleAddRow = (e) => {
    e.preventDefault();
    if (!newLabel) return;
    setRows([
      ...rows,
      {
        id: Date.now(),
        type: newType,
        label: newLabel,
        values: Array(12).fill(0),
      },
    ]);
    setNewLabel('');
    setNewType('revenue');
  };

  // Calculate totals
  const revenueRows = rows.filter(r => r.type === 'revenue');
  const expenseRows = rows.filter(r => r.type === 'expense');
  const revenueTotals = Array(12).fill(0);
  const expenseTotals = Array(12).fill(0);
  const profitLoss = Array(12).fill(0);

  revenueRows.forEach(row => {
    row.values.forEach((v, i) => {
      revenueTotals[i] += v;
    });
  });
  expenseRows.forEach(row => {
    row.values.forEach((v, i) => {
      expenseTotals[i] += v;
    });
  });
  for (let i = 0; i < 12; i++) {
    profitLoss[i] = revenueTotals[i] + expenseTotals[i];
  }

  return (
    <div>
      {/* Heading and sub-description */}
      <div className="mb-4">
        <h2 className="text-xl font-bold text-gray-900">Budget (P&amp;L by Month)</h2>
        <p className="text-gray-600 text-base">Add and manage your revenue and cost lines. Expand/collapse groups to focus on what matters. All values are editable.</p>
      </div>
      {/* Add row controls */}
      <div className="mb-2 flex gap-2 items-end">
        <form className="flex gap-2 items-end" onSubmit={handleAddRow}>
          <select
            value={newType}
            onChange={e => setNewType(e.target.value)}
            className="rounded-md border-gray-300 focus:ring-purple-500 focus:border-purple-500 text-sm"
          >
            <option value="revenue">Revenue</option>
            <option value="expense">Expense</option>
          </select>
          <input
            type="text"
            value={newLabel}
            onChange={e => setNewLabel(e.target.value)}
            placeholder={newType === 'revenue' ? 'Revenue line' : 'Expense line'}
            className="rounded-md border-gray-300 focus:ring-purple-500 focus:border-purple-500 text-sm"
          />
          <button type="submit" className="px-3 py-2 rounded-md bg-purple-600 text-white text-xs font-semibold hover:bg-purple-700">Add</button>
        </form>
      </div>
      {/* Card/table */}
      <div className="bg-white rounded-xl shadow border border-gray-100 p-6">
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm border border-gray-200 rounded-lg">
            <thead>
              <tr className="bg-gray-50">
                <th className="px-3 py-2 text-left font-medium text-gray-500 w-40">Line</th>
                {months.map((m) => (
                  <th key={m} className="px-3 py-2 text-center font-medium text-gray-500">{m}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {/* Revenue group */}
              <tr className="bg-green-50 cursor-pointer group" onClick={() => setExpandRevenue(v => !v)}>
                <td className="px-3 py-2 font-bold text-green-900 flex items-center gap-2">
                  <span className="inline-block w-4">{expandRevenue ? '▼' : '▶'}</span> Revenue
                </td>
                {revenueTotals.map((t, i) => (
                  <td key={i} className="px-2 py-1 text-center text-green-900 font-semibold">{t.toLocaleString()}</td>
                ))}
              </tr>
              {expandRevenue && revenueRows.map((row, rowIdx) => (
                <tr key={row.id} className="bg-white">
                  <td className="px-3 py-2 font-medium text-gray-700 whitespace-nowrap pl-8">{row.label}</td>
                  {row.values.map((val, monthIdx) => (
                    <td key={monthIdx} className="px-2 py-1 text-center">
                      <input
                        type="number"
                        value={val}
                        onChange={e => handleValueChange(rows.indexOf(row), monthIdx, e.target.value)}
                        className="w-20 rounded border-gray-200 text-right px-2 py-1 focus:ring-purple-500 focus:border-purple-500 text-sm"
                      />
                    </td>
                  ))}
                </tr>
              ))}
              {/* Costs group */}
              <tr className="bg-red-50 cursor-pointer group" onClick={() => setExpandCosts(v => !v)}>
                <td className="px-3 py-2 font-bold text-red-900 flex items-center gap-2">
                  <span className="inline-block w-4">{expandCosts ? '▼' : '▶'}</span> Costs
                </td>
                {expenseTotals.map((t, i) => (
                  <td key={i} className="px-2 py-1 text-center text-red-900 font-semibold">{t.toLocaleString()}</td>
                ))}
              </tr>
              {expandCosts && expenseRows.map((row, rowIdx) => (
                <tr key={row.id} className="bg-white">
                  <td className="px-3 py-2 font-medium text-gray-700 whitespace-nowrap pl-8">{row.label}</td>
                  {row.values.map((val, monthIdx) => (
                    <td key={monthIdx} className="px-2 py-1 text-center">
                      <input
                        type="number"
                        value={val}
                        onChange={e => handleValueChange(rows.indexOf(row), monthIdx, e.target.value)}
                        className="w-20 rounded border-gray-200 text-right px-2 py-1 focus:ring-purple-500 focus:border-purple-500 text-sm"
                      />
                    </td>
                  ))}
                </tr>
              ))}
              {/* Profit/Loss row */}
              <tr className="bg-blue-100 font-bold">
                <td className="px-3 py-2 text-blue-900">Profit / Loss</td>
                {profitLoss.map((t, i) => (
                  <td key={i} className="px-2 py-1 text-center text-blue-900">{t.toLocaleString()}</td>
                ))}
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Budget;
