import { useAuth } from '../../../../contexts/AuthContext';
import { getCompanyProfile, upsertCompanyProfile } from '../../../../services/legalService';
import { supabase } from '../../../../lib/supabase';

const CompanyDetails = () => {
  const { currentOrganization } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [companyInfo, setCompanyInfo] = useState({
    name: '',
    companyNumber: '',
    incorporationDate: '',
    registeredOffice: '',
    website: '',
    contactEmail: '',
    vatNumber: '',
    bankProvider: '',
    bankAccountOpened: ''
  });
  const [editForm, setEditForm] = useState(companyInfo);
  const [logoUrl, setLogoUrl] = useState('');
  const fileInputRef = useRef();

  useEffect(() => {
    const fetchProfile = async () => {
      if (!currentOrganization?.organization_id) return;
      const data = await getCompanyProfile(currentOrganization.organization_id);
      if (data) {
        setCompanyInfo({
          name: data.name || '',
          companyNumber: data.company_number || '',
          incorporationDate: data.incorporation_date || '',
          registeredOffice: data.registered_office || '',
          website: data.website || '',
          contactEmail: data.contact_email || '',
          vatNumber: data.vat_number || '',
          bankProvider: data.bank_provider || '',
          bankAccountOpened: data.bank_account_opened || ''
        });
        setEditForm({
          name: data.name || '',
          companyNumber: data.company_number || '',
          incorporationDate: data.incorporation_date || '',
          registeredOffice: data.registered_office || '',
          website: data.website || '',
          contactEmail: data.contact_email || '',
          vatNumber: data.vat_number || '',
          bankProvider: data.bank_provider || '',
          bankAccountOpened: data.bank_account_opened || ''
        });
        setLogoUrl(data.logo_url || '');
      }
    };
    fetchProfile();
  }, [currentOrganization]);

  const handleSave = async () => {
    if (!currentOrganization?.organization_id) return;
    const profile = {
      organization_id: currentOrganization.organization_id,
      name: editForm.name,
      company_number: editForm.companyNumber,
      incorporation_date: editForm.incorporationDate,
      registered_office: editForm.registeredOffice,
      website: editForm.website,
      contact_email: editForm.contactEmail,
      vat_number: editForm.vatNumber,
      bank_provider: editForm.bankProvider,
      bank_account_opened: editForm.bankAccountOpened
    };
    await upsertCompanyProfile(profile);
    setCompanyInfo(editForm);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setEditForm(companyInfo);
    setIsEditing(false);
  };

  const handleInputChange = (field, value) => {
    setEditForm(prev => ({ ...prev, [field]: value }));
  };

  const handleLogoUpload = async (e) => {
    const file = e.target.files[0];
    if (!file || !currentOrganization?.organization_id) return;
    const fileExt = file.name.split('.').pop();
    const filePath = `${currentOrganization.organization_id}/logo.${fileExt}`;
    const { error: uploadError } = await supabase.storage.from('company-logos').upload(filePath, file, { upsert: true });
    if (uploadError) {
      alert('Error uploading logo');
      return;
    }
    const { data: publicUrlData } = supabase.storage.from('company-logos').getPublicUrl(filePath);
    const publicUrl = publicUrlData.publicUrl;
    setLogoUrl(publicUrl);
    await upsertCompanyProfile({ organization_id: currentOrganization.organization_id, logo_url: publicUrl });
  };

  return (
    <div className="w-full">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Company Details</h1>
          <p className="text-gray-600 text-sm mt-1">Manage your company's core information, contact details, and banking setup. This information is used throughout the platform and can be updated at any time.</p>
        </div>
        <button
          onClick={() => setIsEditing(!isEditing)}
          className="flex items-center space-x-1 text-sm text-gray-600 hover:text-gray-800"
        >
          {isEditing ? (
            <>
              <CheckIcon className="h-4 w-4" />
              <span>Save</span>
            </>
          ) : (
            <>
              <PencilIcon className="h-4 w-4" />
              <span>Edit</span>
            </>
          )}
        </button>
      </div>
      {/* Top row: logo + basic info */}
      <div className="w-full flex flex-col md:flex-row gap-8 mb-4">
        {/* Logo upload, 1/3 width */}
        <div className="w-full md:w-1/3 flex flex-col items-center justify-center bg-white rounded-xl shadow p-6">
          <div className="mb-4 w-32 h-32 flex items-center justify-center bg-gray-100 rounded-full overflow-hidden">
            {logoUrl ? (
              <img src={logoUrl} alt="Company Logo" className="object-contain w-full h-full" />
            ) : (
              <span className="text-gray-400 text-4xl font-bold">Logo</span>
            )}
          </div>
          <input
            type="file"
            accept="image/*"
            ref={fileInputRef}
            onChange={handleLogoUpload}
            className="hidden"
          />
          <button
            onClick={() => fileInputRef.current && fileInputRef.current.click()}
            className="px-3 py-1.5 bg-blue-600 text-white rounded-md text-xs hover:bg-blue-700"
          >
            {logoUrl ? 'Change Logo' : 'Upload Logo'}
          </button>
        </div>
        {/* Basic Info, 2/3 width */}
        <div className="w-full md:w-2/3">
          <div className="bg-white rounded-xl shadow p-0 space-y-0 w-full">
            <div className="px-6 pt-6 pb-2">
              <div className="text-lg font-bold text-gray-900 mb-4">Basic Information</div>
            </div>
            <div className="px-6 pb-6 pt-0 space-y-4">
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Company Name</label>
                {isEditing ? (
                  <input type="text" value={editForm.name} onChange={e => handleInputChange('name', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.name}</div>
                )}
              </div>
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Company Number</label>
                {isEditing ? (
                  <input type="text" value={editForm.companyNumber} onChange={e => handleInputChange('companyNumber', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.companyNumber}</div>
                )}
              </div>
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Incorporation Date</label>
                {isEditing ? (
                  <input type="date" value={editForm.incorporationDate} onChange={e => handleInputChange('incorporationDate', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.incorporationDate}</div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="space-y-4">
        {/* Contact Section */}
        <div className="w-full">
          <div className="bg-white rounded-xl shadow p-0 space-y-0 w-full">
            <div className="px-6 pt-6 pb-2">
              <div className="text-lg font-bold text-gray-900 mb-4">Contact</div>
            </div>
            <div className="px-6 pb-6 pt-0 space-y-4">
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Registered Office</label>
                {isEditing ? (
                  <textarea value={editForm.registeredOffice} onChange={e => handleInputChange('registeredOffice', e.target.value)} rows={2} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.registeredOffice}</div>
                )}
              </div>
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Website</label>
                {isEditing ? (
                  <input type="url" value={editForm.website} onChange={e => handleInputChange('website', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.website}</div>
                )}
              </div>
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Contact Email</label>
                {isEditing ? (
                  <input type="email" value={editForm.contactEmail} onChange={e => handleInputChange('contactEmail', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.contactEmail}</div>
                )}
              </div>
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">VAT Number</label>
                {isEditing ? (
                  <input type="text" value={editForm.vatNumber} onChange={e => handleInputChange('vatNumber', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.vatNumber}</div>
                )}
              </div>
            </div>
          </div>
        </div>
        {/* Banking Section */}
        <div className="w-full">
          <div className="bg-white rounded-xl shadow p-0 space-y-0 w-full">
            <div className="px-6 pt-6 pb-2">
              <div className="text-lg font-bold text-gray-900 mb-4">Banking</div>
            </div>
            <div className="px-6 pb-6 pt-0 space-y-4">
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Bank Provider</label>
                {isEditing ? (
                  <input type="text" value={editForm.bankProvider} onChange={e => handleInputChange('bankProvider', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.bankProvider}</div>
                )}
              </div>
              <div>
                <label className="block text-xs font-medium text-gray-500 mb-1">Bank Account Opened</label>
                {isEditing ? (
                  <input type="date" value={editForm.bankAccountOpened} onChange={e => handleInputChange('bankAccountOpened', e.target.value)} className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm" />
                ) : (
                  <div className="text-gray-900 text-sm">{companyInfo.bankAccountOpened}</div>
                )}
              </div>
            </div>
          </div>
        </div>
        {isEditing && (
          <div className="flex justify-end space-x-2 pt-4">
            <button
              onClick={handleCancel}
              className="px-4 py-2 text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
            >
              Cancel
            </button>
            <button
              onClick={handleSave}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Save Changes
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CompanyDetails;
