import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import TopNav from './components/TopNav';
import SecondaryNav from './components/SecondaryNav';
import Dashboard from './pages/Dashboard';
import CompanySetup from './pages/CompanySetup';
import Finance from './pages/Finance';
import CapTable from './pages/CapTable';
import Marketing from './pages/Marketing';
import HR from './pages/HR';

function AppContent() {
  const location = useLocation();
  const [activeTab, setActiveTab] = useState(() => {
    const path = location.pathname;
    if (path === '/finance') return 'Finance';
    if (path === '/cap-table') return 'Cap Table';
    if (path === '/marketing') return 'Marketing';
    if (path === '/hr') return 'HR';
    return 'Company Setup';
  });

  return (
    <div className="min-h-screen bg-gray-50">
      <TopNav />
      <SecondaryNav activeTab={activeTab} setActiveTab={setActiveTab} />
      <main className="container mx-auto px-4 py-8">
        <Routes>
          <Route path="/" element={<CompanySetup />} />
          <Route path="/company-setup" element={<CompanySetup />} />
          <Route path="/finance" element={<Finance />} />
          <Route path="/cap-table" element={<CapTable />} />
          <Route path="/marketing" element={<Marketing />} />
          <Route path="/hr" element={<HR />} />
        </Routes>
      </main>
    </div>
  );
}

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard />;
      case 'company':
        return <CompanySetup />;
      case 'finance':
        return <Finance />;
      case 'captable':
        return <CapTable />;
      case 'marketing':
        return <Marketing />;
      case 'hr':
        return <HR />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
