import React, { useState } from 'react';
import { PlusIcon, TrashIcon, MagnifyingGlassIcon } from '@heroicons/react/24/outline';

const initialPolicies = [
  { id: 1, name: 'Directors & Officers (D&O) Insurance', provider: 'InsureCo', expiryDate: '2024-12-31' },
  { id: 2, name: 'Cybersecurity Insurance', provider: 'CyberGuard', expiryDate: '2025-06-30' },
  { id: 3, name: 'General Liability Insurance', provider: 'SafeSure', expiryDate: '2024-10-01' },
];

const RiskInsurance = () => {
  const [policies, setPolicies] = useState(initialPolicies);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newPolicy, setNewPolicy] = useState({ name: '', provider: '', expiryDate: '' });
  const [searchTerm, setSearchTerm] = useState('');

  const handleAddPolicy = (e) => {
    e.preventDefault();
    if (newPolicy.name && newPolicy.provider && newPolicy.expiryDate) {
      setPolicies([...policies, { ...newPolicy, id: Date.now() }]);
      setNewPolicy({ name: '', provider: '', expiryDate: '' });
      setShowAddForm(false);
    }
  };

  const handleDeletePolicy = (id) => {
    setPolicies(policies.filter(p => p.id !== id));
  };

  const filteredPolicies = policies.filter(p =>
    p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.provider.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="space-y-8">
      <div>
        <h2 className="text-2xl font-bold leading-tight text-gray-900">Risk & Insurance</h2>
        <p className="text-sm text-gray-500 mt-1">Manage your company's insurance policies and assess business risks.</p>
      </div>

      <div className="flex justify-between items-center">
        <div className="flex-1 relative max-w-xs">
          <MagnifyingGlassIcon className="pointer-events-none absolute top-2.5 left-3 h-5 w-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search policies..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md"
          />
        </div>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
        >
          <PlusIcon className="h-5 w-5 mr-2" />
          {showAddForm ? 'Cancel' : 'Add New Policy'}
        </button>
      </div>

      {showAddForm && (
        <div className="bg-white p-6 rounded-lg shadow border border-gray-200">
          <h3 className="text-lg font-medium leading-6 text-gray-900 mb-4">Add New Insurance Policy</h3>
          <form onSubmit={handleAddPolicy} className="space-y-4">
            <div>
              <label htmlFor="policyName" className="block text-sm font-medium text-gray-700">Policy Name</label>
              <input
                type="text"
                id="policyName"
                value={newPolicy.name}
                onChange={(e) => setNewPolicy({ ...newPolicy, name: e.target.value })}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                required
              />
            </div>
            <div>
              <label htmlFor="provider" className="block text-sm font-medium text-gray-700">Provider</label>
              <input
                type="text"
                id="provider"
                value={newPolicy.provider}
                onChange={(e) => setNewPolicy({ ...newPolicy, provider: e.target.value })}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                required
              />
            </div>
            <div>
              <label htmlFor="expiryDate" className="block text-sm font-medium text-gray-700">Expiry Date</label>
              <input
                type="date"
                id="expiryDate"
                value={newPolicy.expiryDate}
                onChange={(e) => setNewPolicy({ ...newPolicy, expiryDate: e.target.value })}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                required
              />
            </div>
            <div className="flex justify-end">
              <button type="submit" className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700">
                Add Policy
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Policy</th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Provider</th>
              <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Expiry Date</th>
              <th scope="col" className="relative px-6 py-3"><span className="sr-only">Actions</span></th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {filteredPolicies.map(policy => (
              <tr key={policy.id}>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{policy.name}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{policy.provider}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{new Date(policy.expiryDate).toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' })}</td>
                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                  <button onClick={() => handleDeletePolicy(policy.id)} className="text-red-600 hover:text-red-900">
                    <TrashIcon className="h-5 w-5" />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default RiskInsurance;
