import React from 'react';
import { PlusIcon } from '@heroicons/react/20/solid';

const DocumentRow = ({ icon, title, description, isAdded }) => {
  return (
    <div className="flex items-center p-4 bg-white border border-gray-200 rounded-lg shadow-sm hover:bg-gray-50 transition-colors duration-150">
      <div className="flex-shrink-0 mr-4">
        {icon}
      </div>
      <div className="flex-grow">
        <h3 className="text-md font-medium text-gray-900">{title}</h3>
        <p className="text-sm text-gray-500">{description}</p>
      </div>
      <div className="flex-shrink-0 ml-4">
        {isAdded ? (
           <svg className="w-5 h-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
          </svg>
        ) : (
          <button className="px-4 py-1.5 text-sm font-medium text-gray-700 bg-gray-100 border border-gray-300 rounded-md hover:bg-gray-200">
            Create
          </button>
        )}
      </div>
    </div>
  );
};

const FoundationDocuments = () => {
  const documents = [
    {
      title: 'Articles of Association',
      description: 'Standard company operating rules',
      isAdded: true,
      icon: <img src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg" alt="" className="h-8 w-8" />
    },
    {
      title: 'Shareholder Agreement',
      description: 'Defines rights and responsibilities',
      isAdded: true,
      icon: <img src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg" alt="" className="h-8 w-8" />
    },
    {
      title: 'Founders\' Agreements',
      description: 'Clarifies roles, equity, and IP',
      isAdded: false,
      icon: <img src="https://tailwindui.com/img/logos/workflow-mark-indigo-600.svg" alt="" className="h-8 w-8" />
    }
  ];

  return (
    <div>
      <h2 className="text-lg font-medium text-gray-900 mb-4">Foundation Documents</h2>
      <div className="space-y-3">
        {documents.map((doc, index) => (
          <DocumentRow key={index} {...doc} />
        ))}
      </div>

      <div className="mt-6">
        <button
          type="button"
          className="w-full flex justify-center items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
        >
          <PlusIcon className="h-5 w-5 text-gray-400" />
          <span className="ml-2">Add Another Document</span>
        </button>
      </div>
    </div>
  );
};

export default FoundationDocuments;
