import React, { useState } from 'react';
import { DocumentTextIcon, MagnifyingGlassIcon, XMarkIcon } from '@heroicons/react/24/outline';

const templateCategories = [
  {
    name: 'Foundation',
    templates: [
      { 
        name: 'Founder Vesting Agreement', 
        content: 'This Founder Vesting Agreement...',
        fields: [
          { key: 'companyName', label: 'Company Name', type: 'text', placeholder: 'Enter company name' },
          { key: 'founderName', label: 'Founder Name', type: 'text', placeholder: 'Enter founder name' },
          { key: 'vestingStartDate', label: 'Vesting Start Date', type: 'date' },
          { key: 'totalShares', label: 'Total Shares', type: 'number', placeholder: 'Enter total shares' },
          { key: 'cliffPeriod', label: 'Cliff Period (months)', type: 'number', placeholder: '12' },
          { key: 'vestingSchedule', label: 'Vesting Schedule', type: 'textarea', placeholder: 'Describe vesting schedule...' }
        ]
      },
      { 
        name: 'Articles of Association', 
        content: 'The Articles of Association for the company...',
        fields: [
          { key: 'companyName', label: 'Company Name', type: 'text', placeholder: 'Enter company name' },
          { key: 'companyNumber', label: 'Company Number', type: 'text', placeholder: 'Enter company number' },
          { key: 'registeredOffice', label: 'Registered Office', type: 'textarea', placeholder: 'Enter registered office address' },
          { key: 'incorporationDate', label: 'Incorporation Date', type: 'date' }
        ]
      },
    ],
  },
  {
    name: 'General',
    templates: [
      { 
        name: 'Non-Disclosure Agreement (NDA)', 
        content: 'This Non-Disclosure Agreement (the "Agreement")...',
        fields: [
          { key: 'disclosingParty', label: 'Disclosing Party', type: 'text', placeholder: 'Enter disclosing party name' },
          { key: 'receivingParty', label: 'Receiving Party', type: 'text', placeholder: 'Enter receiving party name' },
          { key: 'effectiveDate', label: 'Effective Date', type: 'date' },
          { key: 'confidentialityPeriod', label: 'Confidentiality Period (years)', type: 'number', placeholder: '3' },
          { key: 'purpose', label: 'Purpose of Disclosure', type: 'textarea', placeholder: 'Describe the purpose of disclosure...' }
        ]
      },
      { 
        name: 'Master Services Agreement (MSA)', 
        content: 'This Master Services Agreement...',
        fields: [
          { key: 'clientName', label: 'Client Name', type: 'text', placeholder: 'Enter client name' },
          { key: 'serviceProvider', label: 'Service Provider', type: 'text', placeholder: 'Enter service provider name' },
          { key: 'effectiveDate', label: 'Effective Date', type: 'date' },
          { key: 'termLength', label: 'Term Length (months)', type: 'number', placeholder: '12' },
          { key: 'services', label: 'Services Description', type: 'textarea', placeholder: 'Describe the services...' }
        ]
      },
    ],
  },
  {
    name: 'Employee',
    templates: [
      { 
        name: 'Standard Employment Contract', 
        content: 'This Employment Agreement is made between...',
        fields: [
          { key: 'employeeName', label: 'Employee Name', type: 'text', placeholder: 'Enter employee name' },
          { key: 'companyName', label: 'Company Name', type: 'text', placeholder: 'Enter company name' },
          { key: 'startDate', label: 'Start Date', type: 'date' },
          { key: 'position', label: 'Position/Title', type: 'text', placeholder: 'Enter position title' },
          { key: 'salary', label: 'Annual Salary', type: 'number', placeholder: 'Enter annual salary' },
