const Templates = () => {
  const [selectedTemplate, setSelectedTemplate] = useState(templateCategories[0].templates[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showCustomizeModal, setShowCustomizeModal] = useState(false);
  const [customizedData, setCustomizedData] = useState({});
  const [customizedName, setCustomizedName] = useState('');

  const filteredCategories = templateCategories.map(category => ({
    ...category,
    templates: category.templates.filter(template =>
      template.name.toLowerCase().includes(searchTerm.toLowerCase())
    ),
  })).filter(category => category.templates.length > 0);

  const handleCustomize = () => {
    setCustomizedName(`${selectedTemplate.name} - Customized`);
    setCustomizedData({});
    setShowCustomizeModal(true);
  };

  const handleFieldChange = (key, value) => {
    setCustomizedData(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleSaveCustomized = () => {
    // In a real app, this would save to the database
    // For now, we'll just close the modal and show a success message
    console.log('Saving customized contract:', {
      name: customizedName,
      template: selectedTemplate.name,
      data: customizedData
    });

    // You could dispatch an action to add to contracts list here
    alert('Customized contract saved to My Contracts!');
    setShowCustomizeModal(false);
  };

  const CustomizeModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-xl font-bold text-gray-900">Customize {selectedTemplate.name}</h3>
          <button
            onClick={() => setShowCustomizeModal(false)}
            className="text-gray-400 hover:text-gray-600"
          >
            <XMarkIcon className="h-6 w-6" />
          </button>
        </div>

        <div className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Contract Name</label>
            <input
              type="text"
              value={customizedName}
              onChange={(e) => setCustomizedName(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a name for this contract"
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {selectedTemplate.fields?.map(field => (
              <div key={field.key}>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  {field.label}
                </label>
                {field.type === 'textarea' ? (
                  <textarea
                    value={customizedData[field.key] || ''}
                    onChange={(e) => handleFieldChange(field.key, e.target.value)}
                    placeholder={field.placeholder}
                    rows={3}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                ) : (
                  <input
                    type={field.type}
                    value={customizedData[field.key] || ''}
                    onChange={(e) => handleFieldChange(field.key, e.target.value)}
                    placeholder={field.placeholder}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                )}
              </div>
            ))}
          </div>

          <div className="bg-gray-50 p-4 rounded-md">
            <h4 className="text-sm font-medium text-gray-700 mb-2">Preview</h4>
            <div className="text-sm text-gray-600 space-y-2">
              <p><strong>Template:</strong> {selectedTemplate.name}</p>
              <p><strong>Custom Name:</strong> {customizedName}</p>
              {Object.entries(customizedData).map(([key, value]) => (
                <p key={key}>
                  <strong>{key}:</strong> {value}
                </p>
              ))}
            </div>
          </div>

          <div className="flex justify-end space-x-3">
            <button
              onClick={() => setShowCustomizeModal(false)}
              className="px-4 py-2 text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
            >
              Cancel
            </button>
            <button
              onClick={handleSaveCustomized}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Save to My Contracts
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-8">
      <div>
        <h3 className="text-xl font-bold text-gray-900">Templates Library</h3>
        <p className="text-sm text-gray-500 mt-1">Browse and customize pre-built legal templates for your business.</p>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* Left Column */}
        <div className="md:col-span-1 space-y-6">
          <div className="relative">
            <MagnifyingGlassIcon className="pointer-events-none absolute top-3.5 left-4 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search templates..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-11 pr-4 py-2 border border-gray-300 rounded-md"
            />
          </div>
          <div className="space-y-4">
            {filteredCategories.map(category => (
              <div key={category.name}>
                <h3 className="text-xs font-semibold uppercase text-gray-500 mb-2">{category.name}</h3>
                <ul className="space-y-1">
                  {category.templates.map(template => (
                    <li key={template.name}>
                      <button
                        onClick={() => setSelectedTemplate(template)}
                        className={`w-full flex items-center p-3 text-left text-sm rounded-md transition-colors ${
                          selectedTemplate.name === template.name
                            ? 'bg-blue-100 text-blue-700'
                            : 'text-gray-700 hover:bg-gray-100'
                        }`}
                      >
                        <DocumentTextIcon className="h-5 w-5 mr-3" />
                        {template.name}
                      </button>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>

        {/* Right Column */}
        <div className="md:col-span-2">
          <div className="bg-white p-6 rounded-lg shadow border border-gray-200 sticky top-8">
            {selectedTemplate ? (
              <div className="space-y-6">
                <div className="flex justify-between items-center">
                  <h2 className="text-xl font-bold text-gray-900">{selectedTemplate.name}</h2>
                  <button 
                    onClick={handleCustomize}
                    className="px-4 py-2 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700"
                  >
                    Customise Template
                  </button>
                </div>
                <div className="prose prose-sm max-w-none p-4 border rounded-md bg-gray-50 h-96 overflow-y-auto">
                  <p>{selectedTemplate.content}</p>
                  {/* A real implementation would render the full document here */}
                  <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
                </div>
              </div>
            ) : (
              <div className="text-center py-20">
                <p className="text-gray-500">Select a template to preview</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {showCustomizeModal && <CustomizeModal />}
    </div>
  );
};

