import React from 'react';

const MemberNode = ({ member, isRoot = false }) => {
  return (
    <div className={`relative flex flex-col items-center ${isRoot ? '' : 'pt-12'}`}>
      {/* Connecting line (not for root) */}
      {!isRoot && <div className="absolute top-0 left-1/2 w-0.5 h-12 bg-gray-300"></div>}

      {/* Member Box */}
      <div className="relative bg-white border-2 border-gray-200 rounded-lg shadow-md p-4 w-64 z-10">
        <h3 className="text-lg font-bold text-gray-800 text-center">{member.name}</h3>
        <p className="text-primary-600 font-semibold text-center">{member.role}</p>
        <p className="text-sm text-gray-500 text-center mt-1">{member.team}</p>
      </div>

      {/* Render children if they exist */}
      {member.children && member.children.length > 0 && (
        <div className="flex justify-center mt-12 relative">
          {/* Horizontal connecting line */}
          <div className="absolute top-0 left-0 right-0 h-0.5 bg-gray-300"></div>
          {member.children.map((child, index) => (
            <div key={index} className="px-4">
              <MemberNode member={child} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const OrgChart = () => {
  const orgData = {
    name: 'Eleanor Vance',
    role: 'CEO',
    team: 'Executive',
    children: [
      {
        name: 'Marcus Holloway',
        role: 'CTO',
        team: 'Technology',
        children: [
          {
            name: 'Aiden Pearce',
            role: 'Lead Engineer',
            team: 'Engineering',
            children: [
                { name: 'Clara Lille', role: 'Frontend Developer', team: 'Engineering' },
                { name: 'Raymond Kenney', role: 'Backend Developer', team: 'Engineering' },
            ]
          },
          {
            name: 'Javier Vega',
            role: 'Head of Product',
            team: 'Product',
            children: [
                { name: 'Sitara Dhawan', role: 'Product Manager', team: 'Product' },
            ]
          },
        ],
      },
      {
        name: 'Miranda Lawson',
        role: 'COO',
        team: 'Operations',
        children: [
          { name: 'Jacob Taylor', role: 'Operations Manager', team: 'Operations' },
          { name: 'Kasumi Goto', role: 'HR Manager', team: 'Human Resources' },
        ],
      },
    ],
  };

  return (
    <div className="bg-gray-50 p-8 rounded-lg">
       <h2 className="text-2xl font-bold text-gray-900 text-center mb-12">Organization Chart</h2>
       <div className="flex justify-center">
        <MemberNode member={orgData} isRoot={true} />
       </div>
    </div>
  );
};

export default OrgChart;
