import React from 'react';
import { useCapTable } from '../../hooks/useCapTable';
import CapTableContent from './components/captable/CapTableContent';
import RoundHistory from './components/roundhistory/RoundHistory';
import ExitScenarios from './components/exitscenarios/ExitScenarios';
import InvestmentPlanning from './components/investment/InvestmentPlanning';

const CapTable = ({ activeSubTab, onSubTabChange }) => {
  const {
    loading,
    error,
    capTable,
    rounds,
    shareholders,
    shareClasses,
    transactions,
    preferenceTerms,
    loadData,
    loadCapTableAtRound,
    calculateExitScenario,
    addShareholder,
    updateShareholder,
    deleteShareholder,
    addRound,
    updateRound,
    deleteRound,
    addTransaction,
    updateTransaction,
    deleteTransaction
  } = useCapTable();

  const renderContent = () => {
    if (loading) {
      return (
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      );
    }

    if (error) {
      return (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Error loading cap table data</h3>
              <div className="mt-2 text-sm text-red-700">
                <p>{error}</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    switch (activeSubTab) {
      case 'captable':
        return (
          <CapTableContent 
            capTable={capTable}
            rounds={rounds}
            shareholders={shareholders}
            shareClasses={shareClasses}
            onRoundChange={loadCapTableAtRound}
            onRefresh={loadData}
          />
        );
      case 'round-history':
        return (
          <RoundHistory 
            rounds={rounds}
            transactions={transactions}
            shareholders={shareholders}
            onAddRound={addRound}
            onUpdateRound={updateRound}
            onDeleteRound={deleteRound}
            onAddTransaction={addTransaction}
            onUpdateTransaction={updateTransaction}
            onDeleteTransaction={deleteTransaction}
            onRefresh={loadData}
          />
        );
      case 'scenarios':
        return (
          <ExitScenarios 
            capTable={capTable}
            preferenceTerms={preferenceTerms}
            onCalculateScenario={calculateExitScenario}
          />
        );
      case 'investment':
        return (
          <InvestmentPlanning 
            capTable={capTable}
            rounds={rounds}
            shareholders={shareholders}
            onAddRound={addRound}
            onRefresh={loadData}
          />
        );
      default:
        return (
          <CapTableContent 
            capTable={capTable}
            rounds={rounds}
            shareholders={shareholders}
            shareClasses={shareClasses}
            onRoundChange={loadCapTableAtRound}
            onRefresh={loadData}
          />
        );
    }
  };

  return (
    <div className="space-y-6">
      {renderContent()}
    </div>
  );
};

export default CapTable;
