import React, { useState, useMemo } from 'react';
import { CurrencyPoundIcon } from '@heroicons/react/20/solid';

// Mock current cap table data (post Series A)
const currentCapTable = [
  { name: 'Alice', role: 'Founder', shares: 4000000, ownership: 32.0, shareClass: 'Common', originalInvestment: 50000 },
  { name: 'Bob', role: 'Founder', shares: 4000000, ownership: 32.0, shareClass: 'Common', originalInvestment: 50000 },
  { name: 'Seed Fund A', role: 'Investor', shares: 2000000, ownership: 16.0, shareClass: 'Seed Preferred', originalInvestment: 500000 },
  { name: 'Series A Lead', role: 'Investor', shares: 1500000, ownership: 12.0, shareClass: 'Series A Preferred', originalInvestment: 1500000 },
  { name: 'Follow-on Fund', role: 'Investor', shares: 1000000, ownership: 8.0, shareClass: 'Series A Preferred', originalInvestment: 1000000 },
];

const formatCurrency = (value) => `£${(value / 1000000).toFixed(2)}M`;
const formatNumber = (value) => new Intl.NumberFormat('en-GB').format(value);
const formatPercent = (value) => `${value.toFixed(2)}%`;

const ExitScenarios = () => {
  const [exitScenario, setExitScenario] = useState({
    acquisitionAmount: 50000000, // £50M
    acquisitionPercentage: 100, // 100% acquisition
    preferenceType: 'non-participating', // 'non-participating' or 'participating'
  });

  const [preferenceMultipliers, setPreferenceMultipliers] = useState({
    'Seed Fund A': 3, // 3x preference
    'Series A Lead': 4, // 4x preference  
    'Follow-on Fund': 4, // 4x preference
  });

  const totalShares = currentCapTable.reduce((sum, shareholder) => sum + shareholder.shares, 0);

  const exitCalculations = useMemo(() => {
    const { acquisitionAmount, acquisitionPercentage, preferenceType } = exitScenario;
    const actualAcquisitionValue = (acquisitionAmount * acquisitionPercentage) / 100;

    // First, calculate preference payouts
    const preferencePayouts = currentCapTable
      .filter(shareholder => shareholder.shareClass.includes('Preferred'))
      .map(shareholder => {
        const multiplier = preferenceMultipliers[shareholder.name] || 1;
        const preferenceAmount = shareholder.originalInvestment * multiplier;
        const conversionValue = (shareholder.ownership / 100) * actualAcquisitionValue;

        // For non-participating, take the higher of preference or conversion
        // For participating, always take preference + conversion
        let finalValue;
        if (preferenceType === 'non-participating') {
          finalValue = Math.max(preferenceAmount, conversionValue);
        } else {
          // Participating: get preference + conversion
          finalValue = preferenceAmount + conversionValue;
        }

        return {
          name: shareholder.name,
          preferenceAmount,
          conversionValue,
          finalValue,
          tookPreference: preferenceType === 'participating' || preferenceAmount > conversionValue,
        };
      });

    // Calculate remaining proceeds after preferences
    const totalPreferencePayout = preferencePayouts.reduce((sum, p) => sum + p.finalValue, 0);
    const remainingProceeds = Math.max(0, actualAcquisitionValue - totalPreferencePayout);

    // Distribute remaining proceeds to common shareholders
    const commonShareholders = currentCapTable.filter(s => s.shareClass === 'Common');
    const totalCommonOwnership = commonShareholders.reduce((sum, s) => sum + s.ownership, 0);

    return currentCapTable.map(shareholder => {
      if (shareholder.shareClass.includes('Preferred')) {
        const preferencePayout = preferencePayouts.find(p => p.name === shareholder.name);
        return {
          ...shareholder,
          preferenceAmount: preferencePayout.preferenceAmount,
          conversionValue: preferencePayout.conversionValue,
          finalValue: preferencePayout.finalValue,
          tookPreference: preferencePayout.tookPreference,
          multiplier: preferenceMultipliers[shareholder.name] || 1,
        };
      } else {
        // Common shareholders get their pro-rata share of remaining proceeds
        const commonShare = totalCommonOwnership > 0 ? (shareholder.ownership / totalCommonOwnership) * remainingProceeds : 0;
        return {
          ...shareholder,
          preferenceAmount: 0,
          conversionValue: commonShare,
          finalValue: commonShare,
          tookPreference: false,
          multiplier: 1,
        };
      }
    });
  }, [exitScenario, preferenceMultipliers]);

  const totalExitValue = exitCalculations.reduce((sum, calc) => sum + calc.finalValue, 0);

  const handleMultiplierChange = (shareholderName, value) => {
    setPreferenceMultipliers(prev => ({
      ...prev,
      [shareholderName]: parseFloat(value) || 1
    }));
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center space-x-2">
        <CurrencyPoundIcon className="h-6 w-6 text-purple-600" />
        <h2 className="text-xl font-bold text-gray-900">Exit Scenarios</h2>
      </div>

      {/* Exit Scenario Inputs */}
      <div className="bg-white rounded-lg shadow border border-gray-200 p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Exit Parameters</h3>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Acquisition Amount */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Acquisition Amount (£)</label>
            <input
              type="number"
              value={exitScenario.acquisitionAmount}
              onChange={(e) => setExitScenario(prev => ({ 
                ...prev, 
                acquisitionAmount: parseFloat(e.target.value) || 0 
              }))}
              className="w-full rounded-md border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
              placeholder="50000000"
            />
            <p className="text-sm text-gray-500 mt-1">
              {formatCurrency(exitScenario.acquisitionAmount)}
            </p>
          </div>

          {/* Acquisition Percentage */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">% Being Acquired</label>
            <input
              type="number"
              value={exitScenario.acquisitionPercentage}
              onChange={(e) => setExitScenario(prev => ({ 
                ...prev, 
                acquisitionPercentage: parseFloat(e.target.value) || 0 
              }))}
              className="w-full rounded-md border-gray-300 shadow-sm focus:border-purple-500 focus:ring-purple-500"
              placeholder="100"
              min="0"
              max="100"
            />
            <p className="text-sm text-gray-500 mt-1">
              {exitScenario.acquisitionPercentage}% of company
            </p>
          </div>

          {/* Preference Type */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Preference Type</label>
            <div className="bg-purple-100 p-0.5 rounded-lg flex text-xs w-36">
              <button
                onClick={() => setExitScenario(prev => ({ ...prev, preferenceType: 'non-participating' }))}
                className={`px-2 py-1 rounded-md flex-1 text-purple-700 ${exitScenario.preferenceType === 'non-participating' ? 'bg-white shadow-sm' : ''}`}
              >
                Non-Participating
              </button>
              <button
                onClick={() => setExitScenario(prev => ({ ...prev, preferenceType: 'participating' }))}
                className={`px-2 py-1 rounded-md flex-1 text-purple-700 ${exitScenario.preferenceType === 'participating' ? 'bg-white shadow-sm' : ''}`}
              >
                Participating
              </button>
            </div>
            <p className="text-sm text-gray-500 mt-1">
              {exitScenario.preferenceType === 'non-participating' ? 'Take preference OR conversion' : 'Take preference + conversion'}
            </p>
          </div>
        </div>

        {/* Summary */}
        <div className="mt-6 bg-purple-50 p-4 rounded-lg">
          <p className="text-sm text-purple-600 font-medium">Total Exit Value</p>
          <p className="text-2xl font-bold text-purple-900">
            {formatCurrency((exitScenario.acquisitionAmount * exitScenario.acquisitionPercentage) / 100)}
          </p>
        </div>
      </div>

      {/* Exit Cap Table */}
      <div className="bg-white rounded-lg shadow border border-gray-200 overflow-hidden">
        <div className="px-6 py-4 bg-gray-50 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Exit Cap Table</h3>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Shareholder</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Share Class</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Original Investment</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ownership %</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider bg-blue-50">Preference Multiplier</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider bg-yellow-50">Preference Amount</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider bg-orange-50">Conversion Value</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider bg-green-50">Final Exit Value</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {exitCalculations.map((shareholder, index) => (
                <tr key={index}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="font-medium text-gray-900">{shareholder.name}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      shareholder.role === 'Founder' ? 'bg-green-100 text-green-800' :
                      shareholder.role === 'Investor' ? 'bg-blue-100 text-blue-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {shareholder.role}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                    {shareholder.shareClass}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                    {shareholder.originalInvestment > 0 ? formatCurrency(shareholder.originalInvestment) : '-'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                    {formatPercent(shareholder.ownership)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700 bg-blue-50">
                    {shareholder.shareClass.includes('Preferred') ? (
                      <input
                        type="number"
                        value={shareholder.multiplier}
                        onChange={(e) => handleMultiplierChange(shareholder.name, e.target.value)}
                        className="w-16 p-1 rounded border-gray-300 text-center"
                        min="0"
                        step="0.1"
                      />
                    ) : (
                      <span className="text-gray-400">1x (Common)</span>
                    )}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700 bg-yellow-50">
                    {shareholder.shareClass.includes('Preferred') ? formatCurrency(shareholder.preferenceAmount) : '-'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700 bg-orange-50">
                    {formatCurrency(shareholder.conversionValue)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-gray-900 bg-green-50">
                    {formatCurrency(shareholder.finalValue)}
                    {shareholder.shareClass.includes('Preferred') && (
                      <div className="text-xs text-gray-500 mt-1">
                        {shareholder.tookPreference ? 'Took preference' : 'Converted to common'}
                      </div>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
            <tfoot className="bg-gray-50">
              <tr>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700">Totals</td>
                <td className="px-6 py-3"></td>
                <td className="px-6 py-3"></td>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700">
                  {formatCurrency(currentCapTable.reduce((sum, s) => sum + s.originalInvestment, 0))}
                </td>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700">100.00%</td>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700 bg-blue-50">-</td>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700 bg-yellow-50">-</td>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700 bg-orange-50">-</td>
                <td className="px-6 py-3 text-left text-sm font-bold text-gray-700 bg-green-50">
                  {formatCurrency(totalExitValue)}
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white p-4 rounded-lg shadow border border-gray-200">
          <p className="text-sm text-gray-500">Founders Total</p>
          <p className="text-xl font-bold text-green-600">
            {formatCurrency(exitCalculations.filter(s => s.role === 'Founder').reduce((sum, s) => sum + s.finalValue, 0))}
          </p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow border border-gray-200">
          <p className="text-sm text-gray-500">Investors Total</p>
          <p className="text-xl font-bold text-blue-600">
            {formatCurrency(exitCalculations.filter(s => s.role === 'Investor').reduce((sum, s) => sum + s.finalValue, 0))}
          </p>
        </div>
        <div className="bg-white p-4 rounded-lg shadow border border-gray-200">
          <p className="text-sm text-gray-500">Preference Payout</p>
          <p className="text-xl font-bold text-purple-600">
            {formatCurrency(exitCalculations.filter(s => s.shareClass.includes('Preferred')).reduce((sum, s) => sum + s.preferenceAmount, 0))}
          </p>
        </div>
      </div>
    </div>
  );
};

export default ExitScenarios;
