// Stepper Wizard Component
const CompanySetupStepper = ({ open, onClose, initialData, organizationId, onStepComplete }) => {
  const [step, setStep] = useState(0);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  // Step form states
  const [name, setName] = useState(initialData.name || '');
  const [logoUrl, setLogoUrl] = useState(initialData.logo_url || '');
  const fileInputRef = useRef();
  const [companyNumber, setCompanyNumber] = useState(initialData.company_number || '');
  const [incorporationDate, setIncorporationDate] = useState(initialData.incorporation_date || '');
  const [registeredOffice, setRegisteredOffice] = useState(initialData.registered_office || '');
  const [website, setWebsite] = useState(initialData.website || '');
  const [contactEmail, setContactEmail] = useState(initialData.contact_email || '');
  const [linkedinProfile, setLinkedinProfile] = useState(initialData.linkedin_profile || '');
  const [vatNumber, setVatNumber] = useState(initialData.vat_number || '');
  const [bankProvider, setBankProvider] = useState(initialData.bank_provider || '');
  const [bankAccountOpened, setBankAccountOpened] = useState(initialData.bank_account_opened || '');

  const steps = [
    {
      label: 'Company Name & Logo',
      content: (
        <>
          <div className="flex flex-col items-center mb-4">
            <div className="mb-2 w-24 h-24 flex items-center justify-center bg-gray-100 rounded-full overflow-hidden">
              {logoUrl ? (
                <img src={logoUrl} alt="Company Logo" className="object-contain w-full h-full" />
              ) : (
                <span className="text-gray-400 text-2xl font-bold">Logo</span>
              )}
            </div>
            <input
              type="file"
              accept="image/*"
              ref={fileInputRef}
              onChange={async (e) => {
                const file = e.target.files[0];
                if (!file || !organizationId) return;
                const fileExt = file.name.split('.').pop();
                const filePath = `${organizationId}/logo.${fileExt}`;
                const { error: uploadError } = await supabase.storage.from('company-logos').upload(filePath, file, { upsert: true });
                if (uploadError) {
                  setError('Error uploading logo: ' + uploadError.message);
                  return;
                }
                const { data: publicUrlData, error: urlError } = supabase.storage.from('company-logos').getPublicUrl(filePath);
                if (urlError || !publicUrlData?.publicUrl) {
                  setError('Error getting logo URL');
                  return;
                }
                setLogoUrl(publicUrlData.publicUrl);
              }}
              className="hidden"
            />
            <button
              type="button"
              onClick={() => fileInputRef.current && fileInputRef.current.click()}
              className="px-3 py-1.5 bg-blue-600 text-white rounded-md text-xs hover:bg-blue-700 mt-2"
            >
              {logoUrl ? 'Change Logo' : 'Upload Logo'}
            </button>
          </div>
          <input
            type="text"
            value={name}
            onChange={e => setName(e.target.value)}
            placeholder="Enter your company name..."
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            autoFocus
          />
        </>
      ),
      save: async () => {
        setSaving(true); setError('');
        try {
          await upsertCompanyProfile({ organization_id: organizationId, name, logo_url: logoUrl });
          onStepComplete && onStepComplete({ name, logo_url: logoUrl });
        } catch (err) { setError(err.message || 'Failed to save.'); throw err; }
        finally { setSaving(false); }
      }
    },
    {
      label: 'Company Number & Incorporation Date',
      content: (
        <>
          <input
            type="text"
            value={companyNumber}
            onChange={e => setCompanyNumber(e.target.value)}
            placeholder="Company Number"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          />
          <input
            type="date"
            value={incorporationDate}
            onChange={e => setIncorporationDate(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </>
      ),
      save: async () => {
        setSaving(true); setError('');
        try {
          await upsertCompanyProfile({ organization_id: organizationId, company_number: companyNumber, incorporation_date: incorporationDate });
          onStepComplete && onStepComplete({ company_number: companyNumber, incorporation_date: incorporationDate });
        } catch (err) { setError(err.message || 'Failed to save.'); throw err; }
        finally { setSaving(false); }
      }
    },
    {
      label: 'Registered Office & Contact Info',
      content: (
        <>
          <textarea
            value={registeredOffice}
            onChange={e => setRegisteredOffice(e.target.value)}
            placeholder="Registered Office Address"
            rows={2}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          />
          <input
            type="url"
            value={website}
            onChange={e => setWebsite(e.target.value)}
            placeholder="Website"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          />
          <input
            type="email"
            value={contactEmail}
            onChange={e => setContactEmail(e.target.value)}
            placeholder="Contact Email"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          />
          <input
            type="url"
            value={linkedinProfile}
            onChange={e => setLinkedinProfile(e.target.value)}
            placeholder="LinkedIn Profile"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </>
      ),
      save: async () => {
        setSaving(true); setError('');
        try {
          await upsertCompanyProfile({ organization_id: organizationId, registered_office: registeredOffice, website, contact_email: contactEmail, linkedin_profile: linkedinProfile });
          onStepComplete && onStepComplete({ registered_office: registeredOffice, website, contact_email: contactEmail, linkedin_profile: linkedinProfile });
        } catch (err) { setError(err.message || 'Failed to save.'); throw err; }
        finally { setSaving(false); }
      }
    },
    {
      label: 'VAT Registration',
      content: (
        <input
          type="text"
          value={vatNumber}
          onChange={e => setVatNumber(e.target.value)}
          placeholder="VAT Number"
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      ),
      save: async () => {
        setSaving(true); setError('');
        try {
          await upsertCompanyProfile({ organization_id: organizationId, vat_number: vatNumber });
          onStepComplete && onStepComplete({ vat_number: vatNumber });
        } catch (err) { setError(err.message || 'Failed to save.'); throw err; }
        finally { setSaving(false); }
      }
    },
    {
      label: 'Business Bank Account',
      content: (
        <>
          <input
            type="text"
            value={bankProvider}
            onChange={e => setBankProvider(e.target.value)}
            placeholder="Bank Provider"
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          />
          <input
            type="date"
            value={bankAccountOpened}
            onChange={e => setBankAccountOpened(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </>
      ),
      save: async () => {
        setSaving(true); setError('');
        try {
          await upsertCompanyProfile({ organization_id: organizationId, bank_provider: bankProvider, bank_account_opened: bankAccountOpened });
          onStepComplete && onStepComplete({ bank_provider: bankProvider, bank_account_opened: bankAccountOpened });
        } catch (err) { setError(err.message || 'Failed to save.'); throw err; }
        finally { setSaving(false); }
      }
    }
  ];

  if (!open) return null;
  return (
    <div className="fixed inset-0 z-50 flex justify-end items-stretch bg-black bg-opacity-50">
      <div className="h-full w-[700px] max-w-full bg-white shadow-2xl p-8 relative flex flex-col">
        <button
          type="button"
          onClick={onClose}
          className="absolute top-4 right-4 text-gray-400 hover:text-gray-600 text-2xl"
          aria-label="Close"
        >
          &times;
        </button>
        <div className="mb-6">
          <div className="text-xs text-gray-500 mb-2">Step {step + 1} of {steps.length}</div>
          <div className="text-xl font-bold text-gray-900 mb-1">{steps[step].label}</div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div className="bg-blue-500 h-2 rounded-full transition-all duration-300" style={{ width: `${((step + 1) / steps.length) * 100}%` }}></div>
          </div>
        </div>
        <form className="flex-1 flex flex-col justify-between" onSubmit={async e => { e.preventDefault(); await steps[step].save(); if (step < steps.length - 1) setStep(s => s + 1); else onClose(); }}>
          <div>{steps[step].content}</div>
          {error && <div className="text-red-600 text-sm mt-2">{error}</div>}
          <div className="flex justify-between mt-8">
            <button
              type="button"
              onClick={() => setStep(s => Math.max(0, s - 1))}
              className="px-4 py-2 bg-gray-100 text-gray-700 rounded hover:bg-gray-200"
              disabled={step === 0 || saving}
            >
              Back
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              disabled={saving}
            >
              {step === steps.length - 1 ? 'Finish' : 'Next'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Move fetchProfile to top-level
async function fetchProfile(organizationId, setCompanyName, setCompanyNumber, setIncorporationDate, setRegisteredOffice, setWebsite, setContactEmail, setVatNumber, setBankProvider, setBankAccountOpened, setLogoUrl, setLinkedinProfile, setChecklist) {
  if (!organizationId) return;
  const data = await getCompanyProfile(organizationId);
  if (data) {
    setCompanyName(data.name || '');
    setCompanyNumber(data.company_number || '');
    setIncorporationDate(data.incorporation_date || '');
    setRegisteredOffice(data.registered_office || '');
    setWebsite(data.website || '');
    setContactEmail(data.contact_email || '');
    setVatNumber(data.vat_number || '');
    setBankProvider(data.bank_provider || '');
    setBankAccountOpened(data.bank_account_opened || '');
    setLogoUrl(data.logo_url || '');
    setLinkedinProfile(data.linkedin_profile || '');
    setChecklist((prev) => prev.map((item, idx) => {
      switch (idx) {
        case 0: return { ...item, completed: !!(data.name && data.logo_url) };
        case 1: return { ...item, completed: !!(data.company_number && data.incorporation_date) };
        case 2: return { ...item, completed: !!(data.registered_office && data.website && data.contact_email) };
        case 8: return { ...item, completed: !!(data.bank_provider && data.bank_account_opened) };
        case 9: return { ...item, completed: !!data.vat_number };
        default: return item;
      }
    }));
  }
}

