import React, { useState, useEffect } from 'react';
import Card from '../../../../components/common/layout/Card';
import OrgChart from './orgchart/OrgChart';
import { PencilIcon, CheckIcon } from '@heroicons/react/20/solid';
import { useAuth } from '../../../../contexts/AuthContext';
import { getCompanyProfile, upsertCompanyProfile } from '../../../../services/legalService';

const CompanyDetails = () => {
  const { currentOrganization } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [companyInfo, setCompanyInfo] = useState({
    name: '',
    companyNumber: '',
    incorporationDate: '',
    registeredOffice: '',
    website: '',
    contactEmail: '',
    vatNumber: '',
    bankProvider: '',
    bankAccountOpened: ''
  });
  const [editForm, setEditForm] = useState(companyInfo);

  useEffect(() => {
    const fetchProfile = async () => {
      if (!currentOrganization?.organization_id) return;
      const data = await getCompanyProfile(currentOrganization.organization_id);
      if (data) {
        setCompanyInfo({
          name: data.name || '',
          companyNumber: data.company_number || '',
          incorporationDate: data.incorporation_date || '',
          registeredOffice: data.registered_office || '',
          website: data.website || '',
          contactEmail: data.contact_email || '',
          vatNumber: data.vat_number || '',
          bankProvider: data.bank_provider || '',
          bankAccountOpened: data.bank_account_opened || ''
        });
        setEditForm({
          name: data.name || '',
          companyNumber: data.company_number || '',
          incorporationDate: data.incorporation_date || '',
          registeredOffice: data.registered_office || '',
          website: data.website || '',
          contactEmail: data.contact_email || '',
          vatNumber: data.vat_number || '',
          bankProvider: data.bank_provider || '',
          bankAccountOpened: data.bank_account_opened || ''
        });
      }
    };
    fetchProfile();
  }, [currentOrganization]);

  const handleSave = async () => {
    if (!currentOrganization?.organization_id) return;
    const profile = {
      organization_id: currentOrganization.organization_id,
      name: editForm.name,
      company_number: editForm.companyNumber,
      incorporation_date: editForm.incorporationDate,
      registered_office: editForm.registeredOffice,
      website: editForm.website,
      contact_email: editForm.contactEmail,
      vat_number: editForm.vatNumber,
      bank_provider: editForm.bankProvider,
      bank_account_opened: editForm.bankAccountOpened
    };
    await upsertCompanyProfile(profile);
    setCompanyInfo(editForm);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setEditForm(companyInfo);
    setIsEditing(false);
  };

  const handleInputChange = (field, value) => {
    setEditForm(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="space-y-6 w-full">
      <Card>
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-medium text-gray-900">Company Information</h3>
            <button
              onClick={() => setIsEditing(!isEditing)}
              className="flex items-center space-x-1 text-sm text-gray-600 hover:text-gray-800"
            >
              {isEditing ? (
                <>
                  <CheckIcon className="h-4 w-4" />
                  <span>Save</span>
                </>
              ) : (
                <>
                  <PencilIcon className="h-4 w-4" />
                  <span>Edit</span>
                </>
              )}
            </button>
          </div>

          {isEditing ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Company Name</label>
                <input
                  type="text"
                  value={editForm.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Company Number</label>
                <input
                  type="text"
                  value={editForm.companyNumber}
                  onChange={(e) => handleInputChange('companyNumber', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Incorporation Date</label>
                <input
                  type="date"
                  value={editForm.incorporationDate}
                  onChange={(e) => handleInputChange('incorporationDate', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Website</label>
                <input
                  type="url"
                  value={editForm.website}
                  onChange={(e) => handleInputChange('website', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Contact Email</label>
                <input
                  type="email"
                  value={editForm.contactEmail}
                  onChange={(e) => handleInputChange('contactEmail', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">VAT Number</label>
                <input
                  type="text"
                  value={editForm.vatNumber}
                  onChange={(e) => handleInputChange('vatNumber', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-1">Registered Office</label>
                <textarea
                  value={editForm.registeredOffice}
                  onChange={(e) => handleInputChange('registeredOffice', e.target.value)}
                  rows={2}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Bank Provider</label>
                <input
                  type="text"
                  value={editForm.bankProvider}
                  onChange={(e) => handleInputChange('bankProvider', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Bank Account Opened</label>
                <input
                  type="date"
                  value={editForm.bankAccountOpened}
                  onChange={(e) => handleInputChange('bankAccountOpened', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              <div className="md:col-span-2 flex justify-end space-x-2">
                <button
                  onClick={handleCancel}
                  className="px-4 py-2 text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
                >
                  Cancel
                </button>
                <button
                  onClick={handleSave}
                  className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                >
                  Save Changes
                </button>
              </div>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <dt className="text-sm font-medium text-gray-500">Company Name</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.name}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Company Number</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.companyNumber}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Incorporation Date</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.incorporationDate}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Website</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.website}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Contact Email</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.contactEmail}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">VAT Number</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.vatNumber}</dd>
              </div>
              <div className="md:col-span-2">
                <dt className="text-sm font-medium text-gray-500">Registered Office</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.registeredOffice}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Bank Provider</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.bankProvider}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Bank Account Opened</dt>
                <dd className="mt-1 text-sm text-gray-900">{companyInfo.bankAccountOpened}</dd>
              </div>
            </div>
          )}
        </div>
      </Card>

      <div>
        <h3 className="text-lg font-medium text-gray-900 mb-4">Organization Structure</h3>
        <OrgChart />
      </div>
    </div>
  );
};

export default CompanyDetails;
